INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (1, 'Una specie è:', 'Un insieme di organismi tutti uguali tra loro.', 'Una comunità riproduttiva potenziale, i cui membri non possono incrociarsi tra loro e dare prole fertile.', 'Una comunità riproduttiva potenziale, i cui membri possono incrociarsi tra loro e dare prole feconda.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (2, 'Viene definito taxon (plurale taxa):', 'Il poro o i pori dei Poriferi', 'Il flagello unico o multiplo dei flagellati', 'Un gruppo di organismi che condividono una serie di caratteristiche esclusive.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (3, 'Le principali caterie tassonomiche raggruppano gli organismi secondo un preciso ordine. Dalla cateria più inclusiva a quella meno inclusiva, l’ordine è il seguente:', 'Phylum, ordine, classe, famiglia, specie, genere.', 'Phylum, ordine, classe, famiglia, genere, specie.', 'Phylum, classe, ordine, famiglia, genere, specie.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (4, 'Sono ritenute analoghe:', 'due strutture simili per forma e funzione, ma non equivalenti (fonte ancestrale differente).', 'due strutture diverse per forma e funzione, ma equivalenti (fonte ancestrale simile).', 'due strutture simili per forma e funzione, ma equivalenti (fonte ancestrale differente).', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (5, 'Sono ritenute omologhe:', 'due strutture equivalenti (fonte ancestrale comune), anche se diverse per forma e talvolta per funzione.', 'due strutture diverse per forma e funzione, ma equivalenti (fonte ancestrale simile).', 'due strutture non equivalenti (fonte ancestrale differente), anche se diverse per forma e talvolta per funzione.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (6, 'L’ala di un lepidottero e di un uccello sono:', 'sono strutture sia analoghe che omologhe.', 'strutture omologhe', 'strutture analoghe', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (7, 'L’arto anteriore dei tetrapodi si è modificato per assolvere a varie funzioni. Dunque l’ala di pipistrello e di uccello sono:', 'Strutture analoghe in quanto arto anteriore dei tetrapodi.', 'Strutture omologhe in quanto arto anteriore dei tetrapodi.', 'Strutture omologhe in quanto ali, per cui aiutano a determinare le parentele al di sotto del livello dei tetrapodi.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (8, 'Secondo il sistema di Nomenclatura Binomia introdotto da C. Linneo, una specie è indicata da:', 'Due nomi: il genere con l’iniziale maiuscola e il nome specifico', 'Due nomi: il nome specifico con l’iniziale maiuscola e il nome generico', 'Tre nomi: il nome specifico con l’iniziale maiuscola, il nome generico e quello dello scopritore.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (9, 'L’ International Code of Zoological Nomenclature”, è un insieme di rele accettate internazionalmente che assicura:', 'che ogni specie animale sia descritta e nomenclata secondo gli stessi criteri, eliminando anche casi di omonimia.', 'che ogni specie animale sia attribuito un nome comune, internazionalmente riconosciuto.', 'che ogni specie animale sia attribuito sia un nome specifico che comune.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (10, 'Cosa implica la pluricellularità:', 'differenziamento e specializzazione delle cellule per particolari funzioni.', 'i tessuti sono costituiti da diversi tipi cellulari.', 'Più cellule svolno le stesse funzioni con indubbio vantaggio per l’animale.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (11, 'Gli animali possono essere costituiti da:', '3 o più foglietti embrionali', '1 o 2 foglietti embrionali', '2 o 3 foglietti embrionali.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (12, 'Sono definiti triblastici:', 'gli animali costituiti da 3 aperture principali (bocca) del corpo', 'gli animali la cui segmentazione porta alla formazione di 3 blastomeri', 'gli animali costituiti da tre foglietti embrionali', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (14, 'I protostomi sono animali in cui dal blastoporo:', 'deriva la bocca', 'deriva l’ano', 'deriva prima la bocca, poi successivamente l’ano', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (15, 'I deuterostomi sono animali in cui il blastoporo:', 'non diventa bocca, in alcuni diventa ano.', 'diventa bocca', 'diventa l’occhio', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (17, 'Si definiscono acelomati:', 'Gli animali privi di un apparato digerente', 'Gli animali privi di una cavità del corpo', 'Gli animali che possegno un celoma.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (18, 'Lo pseudoceloma origina:', 'Dal blastocele', 'Dall’euceloma', 'Dall’archentero', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (21, 'Il celoma origina:', 'Dal mesoderma', 'Dall’endoderma', 'Dall’ectoderma', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (13, 'Sono definiti diblastici:', 'gli animali che hanno il corpo costituito da endoderma e mesoderma', 'gli animali che hanno il corpo costituito da endoderma ed ectoderma', 'gli animali che hanno il corpo costituito da ectoderma e mesoderma', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (16, 'Il celoma è:', 'La cavità del corpo ripiena di liquidi, posta tra il canale alimentare e la parete del corpo.', 'La cavità dell''apparato digerente ripiena di liquido.', 'La cavità delle nadi in cui sono versati i gameti.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (19, 'Lo pseudoceloma:', 'Non è tappezzato completamente dal mesoderma e gli organi non sono liberi.', 'È tappezzato completamente dal mesoderma e gli organi sono liberi.', 'Non è tappezzato completamente dal mesoderma e gli organi sono liberi.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (20, 'L’euceloma:', 'È tappezzato dall’ectoderma che quindi circonda anche gli organi interni.', 'È tappezzato dal peritoneo che quindi circonda anche gli organi interni.', 'Non è tappezzato dal peritoneo, per cui gli organi interni sono liberi.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (22, 'Quali dei seguenti organismi sono tutti Eucelomati:', 'Artropodi, Cordati, Platelminti', 'Anellidi, Echinodermi, Nematodi', 'Cordati, Molluschi, Anellidi', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (23, 'La simmetria è:', 'Il tipo di ordine che si riscontra nella disposizione del capo  rispetto alle varie parti che costituiscono un organismo.', 'Il tipo di ordine che si riscontra nella disposizione delle varie parti che costituiscono un organismo.', 'Il tipo di ordine che si riscontra nella disposizione dei piani che passano per le varie parti che costituiscono un organismo.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (24, 'Un animale ha simmetria se può:', 'Essere attraversato da 2 o più piani tra loro perpendicolari.', 'Essere attraversato da infiniti piani tra loro paralleli.', 'Essere diviso in metà all’incirca speculari con uno o più piani.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (25, 'Un organismo è asimmetrico quando:', 'nessun piano, comunque orientato, lo divide in 2 parti speculari', 'ogni piano, comunque orientato, lo divide in 2 parti speculari', 'solo due piani, orientati ortonalmente, lo dividono in 2 parti speculari.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (26, 'Negli organismi a simmetria sferica:', 'ogni piano che passa per l’asse oro-aborale divide l’organismo in parti speculari.', 'ogni piano che passa per il centro divide l’organismo in parti speculari.', 'Un piano sagittale che passa per l’asse oro-aborale divide l’organismo in parti speculari.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (27, 'Negli organismi a simmetria raggiata:', 'Tutti i piani passanti per un piano centrale dividono l’animale in parti speculari.', 'ogni piano passante per un punto centrale divide l’animale in parti speculari.', 'ogni piano passante per un asse centrale divide l’animale in parti speculari.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (28, 'Negli organismi a simmetria biradiale:', 'solo 2 piani passanti per l’asse principale e perpendicolari tra loro dividono l’animale in parti speculari.', 'solo 1 piano passanti per l’asse principale divide l’animale in parti speculari.', 'solo 2 piani passanti per il punto principale del corpo dividono l’animale in parti speculari.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (29, 'Negli organismi a simmetria bilaterale:', 'due piani contenenti l’asse antero-posteriore e quello dorso-ventrale dividono l’animale in 2 parti speculari.', 'un solo piano contenente il punto antero-posteriore e quello dorso-ventrale divide l’animale in 2 parti speculari.', 'un solo piano contenente l’asse antero-posteriore e quello dorso-ventrale divide l’animale in 2 parti speculari.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (56, 'Nel ciclo biologico di Plasmodium sp. la zanzara:', 'È l’ospite definitivo', 'È l’ospite intermedio', 'Non è presente nel ciclo.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (58, 'I vacuoli contrattili sono:', 'Organuli cellulari coinvolti nell’osmorelazione.', 'Organuli cellulari tipici di organismi marini.', 'Organuli cellulari coinvolti nell’attività locomotoria.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (59, 'Lo scheletro delle spugne può essere:', 'Calcareo', 'Chitinoso', 'Osseo', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (60, 'Le spugne vitree appartenno alla classe:', 'Calcispongiae', 'Hyalispongiae', 'Demospongiae', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (61, 'Le spugne da bagno (Demospongiae) hanno uno scheletro:', 'Calcareo', 'Vitreo', 'Di spongina', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (64, 'La larva liberamente natante di molte spugne è:', 'La parenchimula', 'La trocofora', 'La planula', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (30, 'Negli organismi a simmetria pentaraggiata:', 'cinque piani perpendicolari tra loro, passanti per l’asse oro-aborale dividono l’individuo in metà speculari.', 'cinque piani comuni nello spazio, ma non passanti per l’asse oro-aborale, dividono l’individuo in metà speculari.', 'cinque piani perpendicolari tra loro, passanti per il punto centrale del copro dividono l’individuo in metà speculari.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (31, 'La metameria è:', 'La tipica organizazione del corpo dei raggiati, in cui i vari metameri sono organizzati intorno all’asse oro-aborale.', 'La ripetizione seriale di segmenti (metameri) simili lun l’asse longitudinale del corpo.', 'La suddivisione del corpo in due metà speculari (metameri) operata da un piano longitudinale.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (32, 'Quali dei seguenti Phyla sono caratterizzati da metameria:', 'Cordati, Artropodi', 'Platelminti, Nematodi', 'Echinodermi, Cnidari', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (33, 'Il processo di cefalizzazione è:', 'La concentrazione di tessuto nervoso ed organi di senso nel capo di animali a simmetria bilaterale.', 'Il progressivo differenziamento di un cervello in animali a simmetria raggiata.', 'Il processo attraverso cui l’encefalo si sviluppa e acquisisce complessità in animali superiori.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (34, 'I Protozoi appartenno al Regno:', 'Protisti', 'Monera', 'Animale', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (35, 'I Procarioti sono:', 'Organismi che hanno una membrana intorno al materiale genetico.', 'Organismi privi di materiale genetico.', 'Organismi privi di membrana intorno al materiale genetico.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (36, 'Gli Eucarioti sono:', 'Organismi il cui materiale genetico non è delimitato da un sistema di membrane che lo separano dal resto della cellula.', 'Organismi il cui materiale genetico è delimitato da un sistema di membrane che lo separano dal resto della cellula.', 'Organismi il cui materiale genetico è privo di qualsiasi organizzazione particolare.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (37, 'Quali tra i seguenti gruppi appartenno ai Protozoi:', 'Turbellari, cestodi e trematodi,', 'Ciliati, sarcodici, flagellati, sporozoi.', 'Olicheti, policheti, irudinei.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (38, 'Tecamebe, foraminiferi, radiolari ed eliozoi sono:', 'Sporozoi', 'Flagellati', 'Sarcodici', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (39, 'Euglena, Trypanosoma, Volvox appartenno al gruppo dei:', 'Flagellati', 'Sporozoi', 'Ciliati', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (40, 'Paramecium e Vorticella appartenno al gruppo dei:', 'Sporozoi o Apicomplexa', 'Flagellati', 'Ciliati', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (41, 'Plasmodium e Toxoplasma appartenno al gruppo dei:', 'Sporozoi o Apicomplexa', 'Ciliati', 'Flagellati', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (42, 'I ciliati hanno:', '1 o più macronuclei e 1 o più micronuclei.', '1 macronucleo e 1 micronuclei.', '1 solo nucleo.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (43, 'Il plasmalemma di radiolari e foraminiferi può:', 'Secernere gusci silicei, calcarei o proteici di rivestimento.', 'Generare amebociti.', 'Secernere spicole con funzione di supporto.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (44, 'Le tricocisti sono:', 'annessi tegumentari aggiuntivi caratteristici dei ciliati, con funzioni difensive/offensive', 'Cellule flagellate che sporno nella cavità del corpo dei poriferi.', 'Cellule deputate al rilevamento del senso dell’equilibrio.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (45, 'La locomozione dei sarcodici avviene tramite:', 'Flagelli', 'Pseudopodi', 'Ciglia', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (46, 'La schizonia e la sporonia sono:', 'Modalità di riproduzione sessuale', 'Modalità di riproduzione asessuale', 'Tipi di scissione binaria.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (47, 'La schizonia forma:', 'Sporozoiti e segue la riproduzione sessuale.', 'Merozoiti e precede la riproduzione sessuale.', 'Gameti prima della fecondazione.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (48, 'La sporonia forma:', 'Sporozoiti e segue la riproduzione sessuale.', 'Sporozoiti e precede la riproduzione sessuale.', 'Merozoiti e segue la riproduzione sessuale.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (51, 'Nel ciclo biologico di Plasmodium sp. si alternano fasi di:', 'Schizonia, Sporonia e Gametonia', 'Schizonia, Sporonia.', 'Sporonia e Gametonia', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (52, 'Nel ciclo biologico di Plasmodium e in altri Sporozoi la meiosi è:', 'Gametica', 'Zitica', 'Intermedia', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (53, 'L’Amebiasi è una parassitosi umana causata da:', 'Un protozoo', 'Un cestode', 'Un artropode ematofa', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (54, 'La leismaniosi e la trypanosomiasi sono causate da quali organismi:', 'Sarcodici', 'Ciliati', 'Flagellati', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (49, 'Gli sporozoi o Apicomplexa sono:', 'Protozoi caratterizzati dalla presenza di ciglia come organi locomotori o per creare un flusso d’acqua verso il citostoma.', 'Metazoi che producono spore.', 'Protozoi parassiti di ridotte dimensioni, caratterizzati dalla presenza di un particolare organo apicale.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (50, 'Alcuni flagellati tipo Euglena sono caratterizzati da:', 'Membrana cellulare rivestita di gusci calcarei o silicei.', 'Presenza di cloroplasti.', 'Numerosi flagelli che sporno in più punti dalla superficie cellulare.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (55, 'La coniugazione è:', 'una modalità di riproduzione sessuale tipica dei Ciliati.', 'una modalità di riproduzione asessuale tipica dei Flagellati.', 'l’alternanza tra riproduzione sessuale ed asessuale tipica dei Foraminiferi.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (57, 'Molti organismi parassiti hanno messo a punto complessi cicli biologici comprendenti ospiti intermedi in quanto:', 'I loro antenati presentavano complessi cicli vitali, che le forme attuali hanno mantenuto', 'La presenza di un ospite intermedio nel ciclo di norma facilita il trasferimento del parassita a un nuovo organismo', 'Non sono in grado di reperire nuovi ospiti con facilità.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (62, 'Qual è il percorso di una particella che entra dall’esterno nel corpo di una spugna ASCONOIDE, la attraversa, ed esce nuovamente all’esterno:', 'Ostio, canale inalante, sponcele, osculo.', 'Ostio, osculo, sponcele canale inalante.', 'Osculo, sponcele canale inalante, ostio.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (63, 'Nei Poriferi caratterizzati da modello strutturale Sycon:', 'I coanociti tappezzano lo sponcele e i pori collegano direttamente l’esterno con la cavità centrale.', 'I coanociti si trovano nei canali radiali e i pori collegano l’esterno con i canali inalanti.', 'I coanociti tappezzano le camere coanocitarie collegate sia con l’esterno, sia con l’interno attraverso una serie di canali.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (65, 'Da quale struttura nel corpo delle spugne le particelle alimentari venno estratte dall’acqua?', 'I pinacociti', 'I coanociti', 'Lo sponcele', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (66, 'Qual è la funzione dello sponcele?', 'Digestione', 'Esalazione acqua', 'Locomozione', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (67, 'L’osculo è:', 'La principale apertura sia inalante che esalante del corpo dei Poriferi.', 'La principale apertura esalante del corpo dei Poriferi.', 'La principale apertura inalante del corpo dei Poriferi.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (68, 'Nelle spugne si distinguono tre strati del corpo con diverse tipologie cellulari:', 'Pinacoderma, coanoderma, mesoila', 'Ectoderma, mesoderma, endoderma', 'Ectoderma, mesoila, endoderma', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (69, 'I Poriferi sono organismi:', 'Vagili, filtratori o raschiatori', 'Sessili e filtratori', 'Sessili o bentonici e raschiatori', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (70, 'Le spicole sono:', 'Elementi scheletrici dei Poriferi', 'Spine presenti all’estremità caudale dei Nematodi che servono per agganciare la femmina durante l’accoppiamento.', 'Le spine che rivestono il corpo degli Asteroidei', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (71, 'Nei Poriferi i gameti sono prodotti da:', 'Esclusivamente dai coanociti', 'Coanociti e archeociti', 'Pinacociti', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (72, 'I Poriferi si riproducono per:', 'Gemmazione e riproduzione sessuale', 'Solo per scissione binaria', 'Per partenogenesi', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (73, 'Nei Poriferi il sistema nervoso:', 'E’ assente. Le cellule comunicano tra di loro e trasferiscono gli stimoli.', 'E’ costituito da una semplice rete di neuroni, organizzata in accordo alla simmetria del corpo.', 'Presenta i primi accenni di cefalizzazione.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (74, 'Gli Cnidari sono animali:', 'Diblastici, acelomati', 'Diblastici, celomati', 'Triblastici, celomati', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (75, 'Gli Cnidari sono caratterizzati da una simmetria:', 'Raggiata o biradiale', 'Biradiale o bilaterale', 'Sferica o raggiata', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (76, 'La simmetria biradiale è', 'una forma specializzata di simmetria bilaterale.', 'una forma specializzata di simmetria pentaradiale', 'una forma specializzata di simmetria raggiata.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (100, 'Il processo di cefalizzazione è avvenuto parallelamente', 'All’acquisizione della cavità del corpo', 'all’acquisizione della simmetria bilaterale.', 'All’acquisizione della metameria', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (77, 'La simmetria biradiale si realizza per:', 'la presenza di strutture pari su di un corpo a simmetria radiale.', 'la presenza di un sasse di simmetria in un corpo a simmetria radiale.', 'la presenza di strutture multiple su di un corpo a simmetria radiale.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (78, 'Idra, Physalia, niemus, Obelia sono rappresentanti della classe:', 'Hydrozoa', 'Scyphozoa', 'Anthozoa', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (79, 'La cavità del corpo degli Cnidari è definita', 'cavità gastrovascolare', 'celoma', 'emoceloma', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (80, 'Lo stadio medusoide degli Idrozoi è:', 'Dominante nel ciclo biologico.', 'Ridotto o assente.', 'Alternato a quello polimorfico.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (81, 'Gli Scifozoi hanno lo stadio medusoide:', 'Prevalente nel ciclo biolgico.', 'Completamente assente.', 'Ridotto solo in parte.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (82, 'Gli Antozoi sono caratterizzati da simmetria:', 'Bilaterale', 'Biradiale', 'Biconvessa', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (83, 'Gli Antozoi presentano:', 'La cavità gastrovascolare divisa in setti.', 'La cavità gastrovascolare provvista di numerose aperture (pori).', 'La cavità gastrovascolare assente.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (84, 'Negli Antozoi lo stadio medusoide:', 'E’ prevalente nel ciclo biologico', 'Assente.', 'Si alterna a quello di polipo.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (85, 'Il polipo è:', 'liberamente natante ed ha la forma di un ombrello', 'cilindrico ed è una forma bentonica sessile.', 'estremamente allungato e caratterizzato da metameria.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (86, 'I gastrozoidi sono:', 'Cellule dell’intestiono degli Anellidi,', 'Ammasso di cellule della cavità gastrica degli Aceli.', 'Polipi specializzati per l’alimentazione nelle colonie polimorfiche.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (87, 'I Sifonofori quali Physalia, sono caratterizzati da:', 'Forme polimorfiche di polipi.', 'Sifoni inalanti ed esalanti.', 'Sifoni e fori per il circolo dell’acqua.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (88, 'Un esempio di idrozoo coloniale, notevolmente polimorfico, che galleggia libero è:', 'Obelia sp.', 'Aurelia aurita', 'Physalia sp.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (89, 'Lo pneumatoforo è:', 'L’anello periorale del sistema acquifero degli Echinodermi.', 'Una polipo specializzato degli Idrozoi coloniali ripieno di gas che serve per il galleggiamento.', 'Il peduncolo dei Nautili che mette in comunicazione le varie camere della conchiglia.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (90, 'La scarica della cnidocisti avviene:', 'Mai. Si tratta di forme di dispersione (cisti).', 'Per contatto con lo cnidociglio.', 'Spontaneamente, secondo un ritmo tipico della specie.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (91, 'Le cnidocisti sono:', 'Cellule urticanti specializzate nell’adesione e offesa.', 'Lo stadio particolare di cisti in cui si ritrovano alcuni Protozoi.', 'Cellule ghiandolari dell’intestino degli Anellidi.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (92, 'Le meduse si muovono secondo quale di queste modalità:', 'per peristalsi', 'mediante locomozione attraverso i tentacoli', 'per propulsione a getto', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (93, 'Il sistema nervoso degli Cnidari è:', 'Diffuso a rete, con casi di concentramento', 'Costituito da un anello periesofageo, nervi radiali e rete sottocutanea.', 'Costituito da un cordone nervoso con nervi in disposizione metamerica.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (94, 'Qual è una caratteristica peculiare del sistema nervoso degli Cnidari:', 'Strutturazione tendente alla cefalizzazione', 'La presenza di numerosi gangli come centri di coordinazione per i loro complessi movimenti', 'Le sinapsi sono bidirezionali, consentono cioè la trasmissione degli impulsi in entrambe le direzioni.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (95, 'Il sistema escretore degli Cnidari è:', 'Assente. L’escrezione avviene per diffusione.', 'Costituito da protonefridi i cui elementi fondamentali sono le cellule a fiamma.', 'Complesso e caratterizzato da una serie di tubuli che si ramificano in tutto il corpo.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (96, 'Gli Cnidari sono caratterizzati dal possedere:', 'Un eso, un endoscheletro e uno scheletro idrostatico.', 'Soltanto da un esoscheletro.', 'Soltanto un idroscheletro.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (97, 'I Platelminti sono animali:', 'Diblastici, acelomati', 'Triblastici, acelomati', 'Tetrablastici, pseudocelomati', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (98, 'I Platelminti sono caratterizzati da:', 'un celoma ampio e a volte settato', 'un celoma ridotto alla cavità delle nadi e intestino', 'assenza del celoma', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (99, 'Il processo di cefalizzazione offre vantaggi:', 'Agli organismi che si muovono secondo una direzione preferenziale.', 'Agli organismi sessili', 'Agli organismi bentonici', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (101, 'Il phylum dei Platelminti comprende quali delle seguenti classi:', 'Idrozoi, Trematodi, Policheti.', 'Crinoidei, Fasmidi, Aplacorfori', 'Turbellari, Trematodi, Cestodi', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (102, 'I Turbellari sono:', 'Organismi che conducono vita libera, salvo qualche raro caso di parassitismo.', 'Forme parassite con ciclo biologico complesso a più ospiti.', 'Organismi in cui la metameria è riconoscibile sia all’esterno sia all’interno del corpo.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (103, 'I Platelminti:', 'Hanno un canale alimentare completo.', 'Hanno un canale alimentare incompleto, talvolta assente.', 'Hanno un canale alimentare caratterizzato da particolari introflessioni per aumentare la superficie assorbente.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (104, 'Lo scolice è:', 'La principale apertura del corpo dei Poriferi.', 'La testa provvista di organi di aggancio ai tessuti dell’ospite di alcuni vermi parassiti.', 'L’organo della locomozione di alcuni Molluschi.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (105, 'Quali strategie alimentari presentano gli Anellidi:', 'Sono filtratori, consumatori non selettivi, predatori o parassiti.', 'Sono solo consumatori non selettivi', 'Sono solo predatori o parassiti', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (106, 'Organismi rappresentativi della classe Cestodi, phylum Platelminti sono:', 'Tenia, Echinococcus', 'Planaria, Dugesia', 'Schistosoma, Fasciola', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (107, 'Nei Platelminti gli scambi gassosi avvenno:', 'Attraverso i polmoni', 'Attraverso la superficie corporea', 'Attraverso le trachee', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (108, 'Nei Platelminti l’escrezione avviene tramite:', 'La superficie corporea.', 'Protonefridi, i cui elementi fondamentali sono le cellule a fiamma.', 'Metanefridi disposti metamericamente.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (109, 'Gli schistosomi presentano:', 'Corpo nastriforme, una testa (scolice) provvista di organi di aggancio, resto del corpo costituito da elementi ripetuti (proglottidi).', 'Ciglia esterne ventrali.', 'Uno spiccato dimorfismo sessuale: il maschio è sensibilmente più grande e presenta un canale lun la superficie ventrale del corpo, nel quale si adagia la femmina.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (110, 'I Nematodi sono caratterizzati da:', 'Simmetria raggiata', 'Simmetria bilaterale', 'Simmetria biradiale', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (111, 'I Nematodi sono animali:', 'Acelomati, metamerici', 'Celomati, ametamerici', 'Pseudocelomati, ametamerici', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (112, 'Il phylum dei Nematodi è suddiviso nelle seguenti classi:', 'Fasmidi e Afasmidi', 'Ofiuroidei e Oloturoidei', 'Olicheti e Policheti', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (113, 'I Nematodi sono:', 'Vermi piatti', 'Vermi cilindrici', 'Vermi segmentati', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (114, 'I Platelminti sono:', 'Vermi piatti', 'Vermi cilindrici', 'Vermi segmentati', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (115, 'In cosa consiste il fenomeno dell’eutelia nei Nematodi:', 'il numero di cellule per ogni specie non è mai costante.', 'il numero di cellule per ogni specie è costante e si stabilisce subito dopo la fase embrionale.', 'il numero di cellule per ogni specie è costante solo nella fase embrionale.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (116, 'Il canale alimentare dei Nematodi è:', 'Rettilineo e completo.', 'Rettilineo e incompleto.', 'Tortuoso e incompleto', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (117, 'La parete del corpo dei Nematodi è rivestita da:', 'Uno strato di muco secreto dalla sottostante epidermide', 'Un guscio chitinoso', 'Una cuticola pluristratificata, secreta dall’epidermide monostratificata.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (118, 'Il sostegno del corpo nei Nematodi è dato:', 'Dalla pressione del liquido celomatico (scheletro idrostatico).', 'Da un endoscheletro costituito da ossicoli', 'Da un esoscheletro calcareo.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (119, 'I Nematodi presentano:', 'Muscolatura longitudinale e trasversale, che garantisce complessi movimenti.', 'Muscolatura solo longitudinale, per cui i movimenti sono limitati (solo ondulatori).', 'Muscolatura solo circolare, importante per lo strisciamento.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (121, 'Gli Anellidi sono:', 'Vermi piatti', 'Vermi cilindrici', 'Vermi segmentati', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (122, 'Gli Anellidi sono organismi:', 'Triblastici, metamerici, celomati', 'Diblastici, ametamerici, acelomati', 'Diblastici, metamerici, pseudocelomati', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (123, 'Gli Anellidi sono animali:', 'Protostomi', 'Deuterostomi', 'Propostomi da larva e deuterostomi da adulti.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (124, 'I parapodi sono:', 'Le prime appendici dei Crostacei', 'Appendici laterali del corpo dei Policheti', 'Particolari forme di estroflessioni della cellula dei Protozoi', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (120, 'Quali dei seguenti organismi sono rappresentativi del phylum dei Nematodi:', 'Palolo, spirografi, lombrico, tubifex, sanguisuga.', 'Planarie, fasciola, schistosomi, tenie.', 'Ascaridi, anchilostomi, ossiuro, trichinelle, filarie.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (125, 'Il sistema digerente nei lombrichi presenta:', 'Solo la bocca e non l’ano.', 'Piastre chitinose per la triturazione.', 'Una struttura per aumentare la superficie assorbente (tiflosole).', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (126, 'Gli Anellidi sono caratterizzati da:', 'assenza di metameria', 'metameria riconoscibile sia all’esterno, sia all’interno del corpo', 'metameria, ma riconoscibile solo all''esterno del corpo', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (127, 'Il palolo, gli spirografi, il lombrico, la sanguisuga sono organismi rappresentativi del phylum:', 'Mollusca', 'Nematoda', 'Anellida', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (128, 'Il clitello, è:', 'La regione periesofagea del sistema acquifero caratteristico degli Echinodermi', 'Una zona ghiandolare cospicua del corpo degli Anellidi', 'L''organo deputato alla percezione del senso chimico nei Molluschi, localizzato nella cavità del mantello.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (129, 'La funzione del clitello è:', 'Di secerne bozzoli per contenere e proteggere le uova.', 'Di contenere e proteggere gli spermatozoi', 'Di secernere teche protettive in condizioni di scarsa umidità', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (130, 'Quali dei seguenti organismi sono rappresentativi del phylum degli Anellidi:', 'Planarie, fasciola, schistosomi, tenie.', 'Palolo, spirografi, lombrico, tubifex, sanguisuga.', 'Ascaridi, anchilostomi, ossiuro, trichinelle, filarie.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (131, 'I metanefridi sono strutture deputate:', 'agli scambi gassosi nei Nematodi', 'all''escrezione negli Anellidi', 'alla percezione sensoriale negli ascidiacei', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (132, 'Negli Anellidi l''apparato circolatorio è:', 'Aperto, con grandi lacune che costituiscono grossi seni', 'Aperto o chiuso. Se chiuso con cuore dorsale.', 'Aperto con vari gradi di complessità o chiuso con vaso dorsale pulsante', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (133, 'Negli Anellidi le setole laterali al corpo sono:', 'numerose, poche o assenti  a seconda della classe', 'mai presenti. Il corpo è cilindrico e liscio.', 'Presenti esclusivamente in una gruppo costituendone un carattere distintivo', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (134, 'Negli Anellidi l''escrezione avviene essenzialmente attraverso:', 'la superficie del copro', 'attraverso strutture dette protonefridi', 'attraverso strutture dette metanefridi', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (135, 'Negli Anellidi il sistema circolatorio è:', 'esclusivamente di tipo aperto', 'esclusivamente di tipo chiuso', 'di tipo chiuso oppure aperto con vari gradi di complessità', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (136, 'Il movimento per peristalsi tipico degli Anellidi avviene per:', 'contrazione e rilassamento ritmico della muscolatura', 'per azione di aggancio delle setole al substrato', 'grazie alla compressione dei liquidi corporei', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (137, 'La locomozione negli Anellidi avviene per:', 'Peristalsi, strisciamento, nuoto o movimento a compasso', 'Solo per peristalsi', 'Solo per strisciamento', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (138, 'Il sistema nervoso degli Anellidi consta di:', 'Un gruppo di gangli anteriori e dei cordoni nervosi diretti anteriomente e posteriormente', 'Un anello periesofageo con una catena nervosa gangliare ventrale.', 'Di un anello nervoso periesofageo, nervi radiali e rete sottocutanea.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (139, 'Lo stadio larvale tipico degli Anellidi è:', 'la palnula', 'la trocofora', 'doliolaria', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (140, 'Olicheti ed Irudinei sono organismi:', 'monoici', 'dioici', 'asessuati', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (141, 'La metameria riscontrata nei lombrichi è una metameria:', 'omonoma', 'eteronoma', 'assente', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (142, 'I Molluschi sono organismi:', 'protostomi, eucelomati', 'deuterostomi, pseudocelomati', 'protostomi, acelomati', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (143, 'La conchiglia nei gasteropodi è:', 'conchiglia costituita da 2 valve', 'unica avvolta a spirale', 'a forma di zanna, aperta ad entrambe le estremità', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (144, 'La conchiglia nei cefalopodi è:', 'Esterna in alcuni, ridotta e interna,  o assente in altri.', 'costituita da 8 piastre embricate.', 'conchiglia unica dorsale senza torsione', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (145, 'Generi rappresentativi della classe dei cefalopodi, phylum Molluschi sono:', 'Littorina, Aplysia, Helix;', 'Sepia, Octopus, Loli', 'Mitylus, Ostrea, Tellina', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (146, 'La radula è:', 'la piastra cribrosa del sistema acquifero degli Echinodermi', 'la forma larvale tipica degli Anellidi', 'una membrana chitinosa nastriforme, provvista di dentelli tipica dei Molluschi', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (147, 'La radula è un organo adatto a:', 'raschiare i vegetali incrostanti, ma in alcune specie può essere modificato per altre attività alimentari', 'raschiare la superficie del corpo di organismi parassitati', 'secernere carbonato di calcio per l''accrescimaento di parti dure', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (148, 'I bivalvi sono organismi:', 'erbivori o detritivori', 'carnivori', 'filtratori', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (149, 'Gli ctenidi sono:', 'elementi scheletrici dei Poriferi', 'le spine caudali dei Nematodi', 'organi deputati agli scambi gassosi nei Molluschi', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (150, 'Nei Molluschi il sostgno del corpo è offerto da:', 'raramente dalla conchiglia, più spesso dall''acqua circolante nel corpo (scheletro idrostatico)', 'uno scheletro interno', 'uno schelto esterno chitinoso', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (151, 'L''osso di seppia ha come funzione:', 'la protezione del corpo', 'il sostegno e il galleggiamento', 'la difesa dai predatori', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (152, 'I cefalopodi si muovono tipicamente per:', 'movimneto a compasso', 'onde muscolari e ciglia del piede su muco.', 'nuoto e propulsione a getto', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (153, 'La fecondazione nei Molluschi:', 'può essere sia esterna, sia interna', 'è tipicamente esterna', 'è tipicamente interna', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (154, 'Gli elementi caratteristici dei Molluschi sono:', 'la conchiglia, il mantello, la radula e il piede. Il piede e la radula possono mancare.', 'la conchiglia, il mantello, la radula e il piede. La conchiglia e la radula possono mancare.', 'la conchiglia, la radula.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (155, 'La torsione dei visceri, visibile all’esterno osservando la conchiglia è una caratteristica dei:', 'echinoidei', 'cirripedi', 'gasteropodi', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (156, 'Nei bivalvi è assente:', 'la conchiglia e il mantello che la secrne', 'il capo e la radula', 'il piede e il capo', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (157, 'Il corpo dei bivalvi è:', 'compresso lateralmente e racchiuso in una conchiglia costituita da due valve', 'globoso o di forma irrelare rivestito da una tunica spesso trasparente', 'suddiviso in 2 regioni (corpo e piede),spesso ricoperto da una diffusa serie di ciglia utili per la filtrazione', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (158, 'Nei cefalopodi il piede:', 'è sviluppato a costituire un sifone inalante ed uno esalante per espellere l’acqua dalla cavità del mantello assicurando la propulsione a getto.', 'è sviluppato a formare un imbuto muscoloso che espelle l’acqua dalla cavità del mantello assicurando la propulsione a getto.', 'è sviluppato a formare un struttura a forma di vanga utile nello scavo', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (159, 'L''imbuto dei cefalopodi è una struttura:', 'analoga al telson degli Artropodi', 'omologa ai parapodi degli Anellidi policheti', 'omologa al piede degli altri Molluschi', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (160, 'Tra i Molluschi i cefalopodi decapodi sono così detti perché:', 'hanno 10 appendici (8 braccia + 2 tentacoli)', 'hanno 10 appendici (8 tentacoli e 2 braccia)', 'hanno 10 appendici (5 braccia + 5 tentacoli)', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (161, 'Nel phylum Mollusca la metameria è:', 'completamente assente (organismi ametamerici)', 'accennata in ogni una delle 7 calssi', 'parzialmente presente in un solo gruppo', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (162, 'Nelle lumache la conchigia è:', 'pesante e complessa, perché concamerata', 'ridotta o assente', 'costituita da più paezzi articolati a costituire una spirale', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (163, 'La respirazione nei Molluschi avviene prevalentemente :', 'mediante strutture branchiali (ctenidi), provviste di ciglia nei bivalvi.', 'attraverso la superficie del corpo', 'attraverso una struttura ampia e muscolosa: il piede', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (164, 'L''apparato circolatorio dei Molluschi è:', 'di tipo aperto (eccetto i cefalopodi).', 'di tipo chiuso (eccetto i cefalopodi).', 'esclusivamente aperto', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (165, 'Lo strato più interno della conchiglia dei Molluschi è:', 'il periostraco', 'lo strato prismatico', 'lo strato madreperlaceo', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (167, 'Nei Molluschi la fecondazione può essere:', 'solo esterna', 'sia interna che esterna', 'solo interna', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (169, 'Gli Artropodi sono organismi:', 'Deuterostomi triblastici, acelomati e metamerici', 'Protostomi, triblastici, celomati e metamerici.', 'Protostomi, diblastici, celomati e ametamerici', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (166, 'Il sistema nervoso dei Molluschi è:', 'Molto variabile. Di rela c’è un anello nervoso periesofageo connesso con 2 paia di cordoni longitudinali', 'una rete nervosa che segue tipicamente la simmetria del corpo', 'costituito in tutti i gruppi da un encefalo complesso e varie coppie di gangli e cordoni di connessione', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (168, 'Nei Molluschi la perla è:', 'il risultato della reazione di difesa di un mollusco contro un corpo estraneo', 'una concrezione di CaCO3 che periodicamente si accumula sotto al mantello', 'una conchigia embrionale che se non asportata divente una nuova conchiglia', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (170, 'Gli Artropodi sono caratterizzati da:', 'metameria omonoma', 'metameria etreronoma', 'assenza di metameria', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (171, 'Negli Artropodi la metameria presenta:', 'tendenza alla fusione dei metameri (tagmatizzazione)', 'tendenza alla separazione dei metameri (atagmatizzazione)', 'tendenza all''indurimento dei metameri (adattamento per il volo)', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (172, 'Gli Artropodi sono così detti perché:', 'Hanno appendici', 'hanno appendici bifide', 'hanno appendici articolate', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (173, 'Caratteri diagnostici del phylum Artropoda sono:', 'presenza di un esoscheletro, appendici articolate, corpo metamerico regionalizzato', 'corpo a sezione circolare, spesso appiattiti ventralmente, appendici laterali più o meno sviluppate.', 'corpo diviso in 5 aree visibili, aspetto coriaceo, talvolta con spine esterne', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (174, 'Nei Chelicerati le zampe sono:', '4 paia', '3 paia', 'svariate coppie', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (175, 'I cheliceri sono:', 'le sete sporgenti dal corpo di alcuni Anellidi', 'gli elementi dell''apparato masticatore di alcuni Echinodermi', 'appendici specializzate di un gruppo di Artropodi', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (176, 'Le appendici del tronco dei Crostacei sono', 'uniramose', 'biramose', 'multiramose', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (177, 'Gli scambi gassosi negli Artropodi avvenno:', 'attraverso la cute, sitemi branchiali, tracheali o polmonari', 'in maggioranza attraverso la superficie del corpo', 'attraverso un sistema tracheale', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (178, 'Il sistema nervoso degli Artropodi è costituito da:', 'un anello nervoso periesofageo, nervi radiali e rete sottocutanea.', 'un encefalo e una catena gangliare ventrale', 'una rete nervosa che interconnette gangli multipli', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (179, 'La cuticola degli Artropodi è', 'monostratificata, costituita per il 70% di “cartilagine"', 'pluristratificata, costituita per il 30 -50% di “chitina”', 'costituita da blocchi articolati di “chitina”', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (180, 'La sclerificazione è:', 'il processo che porta alla fusione dei metameri', 'il processo di formaziona delle articolazioi negli arti articolati', 'il processo di indurimento e scurimento della cuticola neosecreta', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (181, 'La muta è:', 'il processo di eliminazione del vecchio esoscheletro (esuvia)', 'il processo di indurimento e scurimento della cuticola neosecreta', 'il processo di accrescimento e cambiamento degli Artropodi', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (182, 'Coleotteri, Ditteri, Lepidotteri, Imenotteri sono ordini di quale subphylum:', 'Chelicerati', 'Crostacei', 'Atelocerati', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (183, 'Ostracodi, Cirripedi, Copepodi sono classi di quale sphylum:', 'Chelicerati', 'Crostacei', 'Atelocerati', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (184, 'La muta degli Artropodi è un processo sotto il controllo:', 'endocrino', 'nervoso', 'neuro-endocrino', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (185, 'Nella metamorfosi incompleta:', 'la forma adulta è raggiunta progressivamente attraverso una serie di mute.', 'il passaggio dalla larva all’adulto presuppone un sostanziale cambiamento dell’assetto strutturale dell’insetto', 'lo sviluppo è diretto', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (186, 'Gli insetti olometaboli hanno:', 'assenza di metamorfosi', 'una metamorfosi incompleta', 'una metamorfosi completa', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (187, 'Gli Echinodermi sono organismi:', 'Deuterostomi, diblastici, metamerici, celomati', 'Deuterostomi, triblastici, ametamerici, celomati', 'Protostomi, triblastici, metamerici, acelomati', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (188, 'La classe Crinoidea del phylum Echinodermata comprende:', 'i cetrioli di mare', 'i ricci di mare', 'i gigli di mare', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (217, 'Il phylum Chordata comprende:', 'organismi vertebrati', 'sia organismi vertebrati che invertebrati', 'organismi invertebrati', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (219, 'Negli Echinodermi il capo è:', 'opposto alla regione dell''ano', 'circondato da 5 appendici', 'assente', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (189, 'Gli organismi della classe Ophiuroidea del phylum Echinodermata hanno:', 'corpo con disco centrale da cui si dipartono di rela 5 braccia', 'disco centrale con 5 braccia articolate, spesso ramificate.', 'corpo allungato, bocca anteriore contornata da tentacoli retrattili', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (190, 'La lanterna di Aristotele è:', 'complesso apparato di alimentazione degli Echinoidei', 'complesso apparato di alimentazione degli Crinoidei', 'complesso apparato di alimentazione degli Asteroidei', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (191, 'Negli Echinodermi il celoma è:', 'complesso e articolato', 'ridotto alla cavità periviscerale', 'assente', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (192, 'Lo scheletro negli Echinodermi è:', 'assente', 'interno', 'esterno', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (193, 'La simmetria degli Echinodermi è:', 'raggiata primaria', 'raggiata secondaria', 'raggiata terziaria', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (194, 'Si definisce regione aborale negli Echinodermi:', 'quella dove è presente la bocca', 'quella dove sporno i pedicelli ambulacrali', 'quella dove è presente l''ano', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (195, 'Quanti canali laterali ha il sistema acquifero di un asteroideo:', 'numerosi', 'cinque', 'uno, anulare', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (196, 'Quanti sono i canali radiali del sistema acquifero di un asteroideo:', 'uno, anulare', 'cinque', 'numerosi', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (197, 'Il canale petroso del sistema acquifero di un asteroideo collega:', 'il canale anulare ai canali radiali', 'i canali radiali a quelli laterali', 'il canale anulare alla piastra madreporica', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (198, 'Il sistema acquifero di un asteroideo:', 'è un sistema idraulico tenuto in pressione e ben isolato', 'comunica all''esterno con l''acqua di mare circostante', 'ha tutta una serie di aperture preferenziali (fenditure) per lo scambio di liquidi con l''esterno', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (199, 'I Cordati sono organismi:', 'protostomi, diblastici, pseudocelomati', 'deuterostomi, triblastici, acelomati', 'deuterostomi, triblastici, eucelomati', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (200, 'Il celoma nei Cordati invertebrati è:', 'ridotto o assente', 'ampio e spazioso', 'concamerato', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (201, 'Nei Cordati, la corda si dispone tipicamente:', 'dorsalmente al tubo neurale', 'ventralmente al tubo neurale', 'ventralmente nella coda', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (202, 'Nei Cefalocordati la corda:', 'è presente solo nella porzione caudale del corpo e durante le fasi larvali', 'termina a metà dell’encefalo circa', 'si estende per tutta la lunghezza del corpo', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (203, 'Negli Urocordati la corda:', 'termina a metà dell’encefalo circa', 'si estende per tutta la lunghezza del corpo', 'è presente solo nella porzione caudale del corpo e durante le fasi larvali', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (204, 'Negli Emicefalocordati le vertebre ed il sistema muscolare sono:', 'metamerici', 'si estendono solo nella porzione caudale del corpo', 'assenti', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (205, 'Nelle Ascidie adulte (Urocordati) la corda è:', 'disposta dorsalmente al tubo neurale', 'si estende per tutta la lunghezza del corpo', 'assente', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (206, 'Qual è il meccanismo di alimentazione delle Ascidie:', 'predazione', 'filtrazione', 'raschiatori di substrato', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (207, 'Gli Urodeli comprendono:', 'lucertole e serpenti', 'rane e rospi', 'tritoni e salamandre', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (208, 'Negli Idrozoi il corpo:', 'è sottile e cilindrico e si assottiglia ad entrambe le estremità', 'è un tubo cilindrico con l''estremità inferiore chiusa per formare un disco pedale', 'presenta una superficie superiore convessa e una inferiore concava', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (209, 'Le Spugne possono essere definite come:', 'animali complessi, che occupano un gradino alto nella scala evolutiva', 'aggregati cellulari che costituiscono tessuti e questi organi', 'aggregati cellulari privi di organi', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (210, 'L''unità fondamentale della classificazione zoologica è:', 'la nomenclatura', 'la tassonomia', 'la specie', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (211, 'Due o più specie con certi caratteri in comune formano:', 'un genere', 'una famiglia', 'un ordine', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (212, 'Le famiglie sono raggruppate i', 'generi', 'ordini', 'classi', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (213, 'Tutti i phyla nel loro insieme costituiscono:', 'un super phylum', 'un super regno', 'il regno animale', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (215, 'La toxoplasmosi è una malattia causata da:', 'un animale', 'più animali', 'un protista', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (216, 'Per il gruppo dei Ciliati ha un importante valore tassonomico:', 'la forma della cellula, perché variabile', 'la struttura e la  disposizione delle ciglia', 'la natura chimica dei loro gusci protettivi', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (214, 'Viene definito monoico:', 'un animale provvisto di organi sessuali sia maschili sia femminili in un unico individuo', 'un animale provvisto di un solo organo sessuale', 'un animale sprovvisto di organi sessuali', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (218, 'Il phylum Chordata è:', 'il più ampio e più importante ecologicamente nella linea evolutiva dei protostomi', 'il più ampio e più importante ecologicamente nella linea evolutiva dei deuterostomi', 'il più ampio e più importante ecologicamente nella linea evolutiva dei vertebrati', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (220, 'Il glochidio è:', 'il metamero finale del corpo degli anellidi', 'l''organo di senso specializzato degli Cnidari', 'lo stadio larvale di alcuni bivali dulciacquicoli', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (221, 'E'' definito umbone:', 'il piede di alcuni Molluschi', 'un organismo di grosse dimensioni che produce spermatofore tra i policheti', 'il punto in cui inizia l''accrescimento della conchiglia dei bivalvi', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (222, 'Cosa intendiamo per respirazione cellulare:', 'le molteplici contrazioni a cui una cellula è soggettta per le attività metaboliche', 'tutte le reazioni metaboliche che liberano energia per compiere lavoro a livello cellulare', 'meccanismo di ventilazione di polmoni e branchie', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (223, 'Si definisce nutrizione olozoica:', 'assorbimento di sostanze dall’ambiente', 'ingestione di materiali organici e altri organismi (maggioranza animali).', 'alimentazione a spese di autotrofi', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (224, 'Per sopravvivere e riprodursi un animale deve:', 'procurarsi nell’alimento meno energia di quanta ne spende nel mantenimento.', 'procurarsi la stessa energia di quanta ne spende nel mantenimento.', 'procurarsi nell’alimento più energia di quanta ne spende nel mantenimento.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (225, 'Sfruttando diversi tipi di alimento gli animali:', 'riducono la competizione per le risorse', 'mangiano di più', 'hanno una dieta varia, importante per l''accrescimento', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (226, 'Come avviene la digestione negli CNIDARI?', 'grazie ad enzimi prodotti dalle cellule ghiandolari gastrodermiche (digestione extracellullare)', 'assorbono le sostanze nutritive attraverso la cute', 'presenza di ghiandole digestive e stilo cristallino.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (227, 'Nei PORIFERI la digestione avviene:', 'extracellularmente', 'intarcellularmente', 'mai. C''è solo acqua in transito nel loro corpo', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (228, 'Nei PLATELMINTI l’apparato digerente è:', 'completo con vari gradi di complessità', 'completo o assente', 'incompleto (manca ano) o è assente', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (229, 'Quali fattori influenzano gli scambi gassosi', 'Fattori fisici, biologici ed ambientali', 'Fattori fisici ed ambieltali', 'Fattori biologici ed ambientali', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (230, 'Secondo la legge di Fick, la quantità di gas che attraversa una superficie nell’unità di tempo è:', 'inversamente proporzionale all’aria della superficie e alla differenza tra la pressione parziale ai due lati della superficie, ma direttamente proporzionale allo spessore della membrana', 'direttamente proporzionale all’aria della superficie e alla differenza tra la pressione parziale ai due lati della superficie, ma inversamente proporzionale allo spessore della membrana', 'inversamente proporzionale all’aria della superficie, direttamente proporzionale alla differenza tra la pressione parziale ai due lati della superficie, ma direttamente proporzionale allo spessore della membrana', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (231, 'In un animale aumentando la dimensione:', 'la superficie di scambio aumenta, e diminuisce lo spessore che i gas devono attraversare (distanza di diffusione).', 'il volume di scambio diminuisce, ed aumenta la superfice che i gas devono attraversare (distanza di diffusione).', 'la superficie di scambio diminuisce, ed aumenta lo spessore che i gas devono attraversare (distanza di diffusione).', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (295, 'Qual è il neurormone coinvolto nei processi di muta degli insetti:', 'ormone cerebrale', 'ormone inibitore della muta', 'ecdisone', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (298, 'Alcuni Anellidi quali il Palolo e Autolytus purpeomaculatus possono riprodursi asessualmente per:', 'poliembronia', 'strobilazione', 'gemmazione', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (299, 'La strobilazione è una modalità di riproduzione tipica degli:', 'anellidi', 'scifozoi', 'imenotteri', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (232, 'L''efficienza degli scambi gassosi può aumentare:', 'se il corpo è piatto e sottile o molto stretto e allungato', 'se aumentano le dimensioni lineari, così che il volume del corpo diminuisce', 'se diminuisce la superficie e aumenta lo spessore che i gas devono attraversare (distanza di diffusione).', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (233, 'Lo scambio in controcorrente è uno degli adattamenti più generali per:', 'migliorare l''efficienza dell''assorbimento alimentare a livello intestinale', 'migliorare l''efficienza degli scambi gassosi, principalmente nell''ambiente acquatico.', 'migliorare l''efficienza dell''escrezione in ambiente terrestre', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (234, 'Le esigenze di ossigeno sono correlate in primo luo:', 'alla taglia dell''organismo', 'allo sforzo riproduttivo', 'al grado di attività dell''organismo', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (235, 'La complessità dei sistemi deputati allo scambio gassoso è strettamente correlata:', 'al metabolismo', 'all''escrezione', 'alla circolazione', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (236, 'Le tracheobranchie sono strutture deputate agli scambi gassosi in quali animali:', 'anellidi larvali terrestri', 'insetti larvali acquatici', 'molluschi alla metamorfosi', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (237, 'Quali tra questi organismi presenta respirazione attraverso la superfice corporea:', 'Pesci, Anfibi, Artropodi', 'Tunicati, Nematodi, Crostacei', 'Poriferi, Platelminti, Anellidi, Anfibi', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (238, 'Qual è la funzione delle lamelle branchiali delle branchie dei pesci:', 'aumentano notevolmente l’area della superficie deputata allo scambio gassoso.', 'aumentano notevolmente il volume coinvolto nello scambio gassoso.', 'trattenno le particelle alimentari al passaggio dell''acqua', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (239, 'Nelle branchie dei pesci l''acqua:', 'fluisce nello stesso verso del flusso del sangue', 'fluisce in un verso opposto al flusso del sangue', 'non fluisce in un verso opposto al flusso del sangue', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (240, 'I Molluschi presentano polmoni?', 'No. Sono tutti organismi ad adattamento acquatico', 'Solo polmoni a libro o lamellari nelle specie terrestri', 'Si. È un unico sacco interno altamente vascolarizzato', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (241, 'Nei polmoni degli uccelli il flusso dell''aria è:', 'unidirezionale, grazie alla presenza di sacchi aeriferi', 'bidirezionale, determinando un completo sfruttamento del volume d''aria.', 'unidirezionale durante il volo (movimenti respiratori coordinati con i movimenti battenti delle ali) poi bidirezionale.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (242, 'I polmoni degli uccelli sono:', 'costituiti da 15/20 lamelle fogliformi orizzontali', 'costituiti da numerosi canali paralleli, i parabronchi', 'sacciformi e concamerati', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (243, 'La circolazione dei fluidi organici è essenziale per:', 'la turgidità del corpo', 'il mantenimento dell’equilibrio omeostatico di tutti gli organismi viventi', 'mantenere umide tutte le superfici corporee', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (244, 'Cosa intendiamo per emolinfa:', 'plasma liquido + cellule libere', 'plasma liquido + sangue + cellule libere', 'sangue + liquidi interstiziali', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (245, 'Qual è la funzione degli amebociti nelle spugne?', 'la digestione delle particelle catturate dai coanociti', 'di trasportare ossigeno grazie alla presenza di emocianina', 'di trasportare sostanze nutritive, gas e sostanze di rifiuto.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (246, 'Cosa circola nella cavità gastrovascolare:', 'alimento e acqua ambientale', 'alimento ingerito', 'nulla di esterno, la cavità non ha contatti con l''esterno', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (247, 'Qual è la funzione dello sponcele?', 'Digestione', 'Esalazione acqua', 'Locomozione', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (248, 'Quali dei seguenti organismi hanno un apparato circolatorio aperto:', 'spugne, cnidari, nematodi', 'cefalopodi, anfiosso, alcuni protozoi', 'alcuni anellidi, insetti, crostacei, molti molluschi', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (249, 'Nei cefalopodi:', 'è presente un cuore tubulare', 'è presente un cuore sistemico, e cuori branchiali aggiuntivi', 'è assente un cuore centrale, ma presenti bulbilli branchiali', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (250, 'Negli insetti l''emocele è:', 'diviso in molti seni: periviscerale, perineuralepericardico', 'tipicamente u''unica grossa cavità ampia e spaziosa', 'assente', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (252, 'Negli animali iposmotici i liquidi organici hanno pressione osmotica:', 'minore di quella ambientale', 'uguale a quella ambientale', 'maggiore di quella ambientale', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (251, 'Sono definiti iperosmotici gli animali:', 'i cui liquidi organici hanno complessivamente una pressione osmotica minore dei liquidi ambientali', 'i cui liquidi organici hanno complessivamente una pressione osmotica uguale ai liquidi ambientali', 'i cui liquidi organici hanno complessivamente una pressione osmotica maggiore dei liquidi ambientali', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (253, 'I vacuoli contrattili dei sarcodici:', 'sono strutture per espellere alimento in eccesso, introdotto attraverso il citostoma.', 'sono strutture per trattenere l’acqua nella cellula; cospicui nelle forme marine.', 'sono strutture per espellere l’acqua in eccesso; assenti nelle forme marine e parassite', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (254, 'Nei Poriferi il principale prdotto di rifiuto azotato è:', 'l''ammonica', 'l''urea', 'la xantina', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (255, 'I Platelminti eliminano eccesso d’acqua e altre scorie metaboliche attraverso:', 'cellule che pescano nel celoma ed espellono i metaboliti attraverso un poro', 'un sitema escretore, costituito da protonefridi', 'attraverso tubuli malpighiani', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (256, 'Qual è il principale prodotto azotato di rifiuto degli Anellidi:', 'solo ammoniaca', 'le specie acquatiche espellono urea, quelle terrestri acido urico.', 'ammoniaca in quelli acquatici, urea in quelli terrestri.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (257, 'I metanefridi sono:', 'tubuli ad estremità aperta che pescano nel celoma', 'tubuli ad estremità aperta che pescano tra gli interstizi del corpo', 'tubuli ad estremità chiusa,che si bagnano nell’emolinfa e che si svuotano nell’intestino posteriore.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (258, 'I tubuli malpighiani sono:', 'tubuli ad estremità aperta che pescano nell''ampio celoma', 'tubuli ad estremità chiusa, che si bagnano nell’emolinfa e che si svuotano nell’intestino posteriore (addome).', 'tubuli ad estremità chiusa, che si bagnano nell’emolinfa e che si svuotano nell’intestino posteriore.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (259, 'Ghiandole antennali, mascellari o coxali, sono strutture deputate all''escrezione in quali organismi:', 'Anellidi', 'Crostacei, Aracnidi', 'Chilopodi, diplopodi', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (260, 'Negli Artropodi terrestri il principale prodotto di rifiuto azotato è:', 'l''urea', 'l''ammoniaca', 'l''acido urico', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (261, 'La principale funzione del sistema nervoso centrale è di:', 'integrare l’afferenza sensoriale in base alle esigenze: valutare, confrontare ed elabora le risposte.', 'raccoglie informazioni esterne ed interne al corpo dell’animale, trasformandole in stimoli', 'raccoglie informazioni esterne ed interne al corpo dell’animale, trasformandole in impulsi', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (262, 'Nei neuroni, i prolungamenti citoplasmatici dendritici conducono:', 'impulsi in direzione centrifuga', 'impulsi in direzione centripeta', 'impulsi in direzione sia centripeta che centrifuga', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (263, 'La conduzione dell''impulso nervoso può essere velocizzata attraverso:', 'aumento del calibro degli assoni (assoni giganti)', 'aumento del numero degli assoni (assoni multipli)', 'aumento del neurotrasmettitore rilasciato', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (264, 'L’impulso nervoso procede:', 'nelle regioni refrattarie (propagazione rapida)', 'rapidamente in tutte le direzioni (propagazione multi direzionale)', 'solo nelle regioni non refrattarie (propagazione unidirezionale).', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (265, 'Esiste una stretta relazione tra la complessità del sistema nervoso e:', 'organizzazione corpo e simmetria', 'numero di organi di senso', 'la presenza del capo', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (266, 'Un sistema nervoso ben sviluppato è associato solitamente a:', 'abitudini sessili, per la sua particolare disposizione che segue la simmetria', 'motilità ed una cospicua dimensione del corpo.', 'semplici funzioni di tutti gli organi', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (289, 'L''occhio del Nautilus (Mollusco, Cefalopode) è:', 'ad una sola lente', 'provvisto di retina ma non di lente', 'privo di lente e retina', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (290, 'Le macchie oculari sono organi fotorecettori:', 'ad una sola lente', 'provvisti di retina ma non di lente', 'privi di lente e retina', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (292, 'L’occhio dei vertebrati è definito:', 'converso', 'everso', 'inverso', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (293, 'Gli ommatidi sono unità funzionali di quale struttura:', 'dell''epitelio gastrico degli anellidi', 'dell''occhio composto degli artropodi', 'delle strutture escretrici dei platelminti', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (267, 'Negli animali a simmetria raggiata il sistema nervoso è tipicamente organizzato:', 'tutt''intorno ai primi tratti dell''apparato digerente', 'con tendenza ad una cefalizzazione anche se accennata', 'in modo che le cellule nervose formano una rete nervosa.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (268, 'Negli animali a simmetria bilaterale il sistema nervoso è organizzato:', 'concentrato intorno ai tratti dell''apparato digerente a costituire grossi gangli', 'in modo che le cellule nervose formano una rete nervosa”.', 'con tendenza ad una cefalizzazione', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (269, 'Il processo di cefalizzazione è:', 'La concentrazione di tessuto nervoso ed organi di senso nel capo di animali a simmetria bilaterale.', 'Il progressivo differenziamento di un cervello in animali a simmetria raggiata.', 'Il processo attraverso cui l’encefalo si sviluppa e acquisisce complessità in animali superiori.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (270, 'La schizonia e la sporonia sono:', 'Modalità di riproduzione sessuale', 'Modalità di riproduzione asessuale', 'Tipi di scissione binaria.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (271, 'La schizonia forma:', 'Sporozoiti e segue la riproduzione sessuale.', 'Gameti prima della fecondazione.', 'Merozoiti e precede la riproduzione sessuale.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (272, 'Nel ciclo biologico di Plasmodium sp. si alternano fasi di:', 'Sporonia e Gametonia', 'Schizonia, Sporonia.', 'Schizonia, Sporonia e Gametonia', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (273, 'Nei sistemi nervosi definiti "avanzati"', 'Non esiste il SNC', 'le cellule formano fitte reti nervose ed è presente un complesso sistema periferico', 'esiste sia un sitema nervoso centrale che periferico', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (274, 'Una notevole complessità del sistema nervoso viene raggiunta con la comparsa di:', 'sinapsi bidirezionali e gangli sparsi in ogni regione del corpo', 'un aumento delle tipologie cellulari: astrociti, olidendrociti, cellule di schwa', 'concentrazione dei corpi cellulari a costituire gangli, particolarmente nella regione del capo, e comparsa delle sinapsi e nervi.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (275, 'La larva liberamente natante di molte spugne è:', 'la parenchimula', 'la trocofora', 'la planula', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (276, 'I Poriferi si riproducono per:', 'Per partenogenesi', 'Solo per scissione binaria', 'Gemmazione e riproduzione sessuale', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (277, 'Nei Poriferi il sistema nervoso:', 'E’ costituito da una semplice rete di neuroni, organizzata in accordo alla simmetria del corpo.', 'E’ assente. Le cellule comunicano tra di loro e trasferiscono gli stimoli.', 'Presenta i primi accenni di cefalizzazione.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (278, 'Lo stadio medusoide degli Idrozoi è:', 'Dominante nel ciclo biologico.', 'Alternato a quello polimorfico.', 'Ridotto o assente.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (279, 'Il sistema nervoso degli Cnidari è:', 'Diffuso a rete, con casi di concentramento', 'Costituito da un anello periesofageo, nervi radiali e rete sottocutanea.', 'Costituito da un cordone nervoso con nervi in disposizione metamerica.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (280, 'Qual è una caratteristica peculiare del sistema nervoso degli Cnidari:', 'Strutturazione tendente alla cefalizzazione', 'La presenza di numerosi gangli come centri di coordinazione per i loro complessi movimenti', 'Le sinapsi sono bidirezionali, consentono cioè la trasmissione degli impulsi in entrambe le direzioni.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (281, 'Il processo di cefalizzazione offre vantaggi:', 'Agli organismi sessili', 'Agli organismi che si muovono secondo una direzione preferenziale.', 'Agli organismi bentonici', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (282, 'Il processo di cefalizzazione è avvenuto parallelamente:', 'all’acquisizione della cavità del corpo', 'all’acquisizione della simmetria bilaterale.', 'all’acquisizione della metameria', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (283, 'Quali strategie alimentari presentano gli Anellidi:', 'Sono solo consumatori non selettivi', 'Sono solo predatori o parassiti', 'Sono filtratori, consumatori non selettivi, predatori o parassiti.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (284, 'Il sostegno del corpo nei Nematodi è dato:', 'Dalla pressione del liquido celomatico (scheletro idrostatico).', 'Da un endoscheletro costituito da ossicoli', 'Da un esoscheletro calcareo.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (288, 'La muta degli Artropodi è un processo sotto il controllo:', 'endocrino', 'nervoso', 'neuro-endocrino', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (285, 'Il sistema nervoso degli Anellidi consta di:', 'Un gruppo di gangli anteriori e dei cordoni nervosi diretti anteriomente e posteriormente', 'Di un anello nervoso periesofageo, nervi radiali e rete sottocutanea.', 'Un anello periesofageo con una catena nervosa gangliare ventrale.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (286, 'Il sistema nervoso dei Molluschi è:', 'Molto variabile. Di rela c’è un anello nervoso periesofageo connesso con 2 paia di cordoni longitudinali', 'una rete nervosa che segue tipicamente la simmetria del corpo', 'costituito in tutti i gruppi da un encefalo complesso e varie coppie di gangli e cordoni di connessione', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (287, 'Il sistema nervoso degli Artropodi è costituito da:', 'un anello nervoso periesofageo, nervi radiali e rete sottocutanea.', 'un encefalo e una catena gangliare ventrale', 'una rete nervosa che interconnette gangli multipli', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (291, 'L''accomodazione è quel meccanismo attraverso cui:', 'la forma del cristallino o la sua posizione variano per formare immagini nitide', 'l''occhio si adatta alle diverse condizioni di luminosità', 'il pigmento visivo (oopsina) viene scisso e per la visione', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (294, 'Le statocisti sono:', 'organi che rispondono all’accellerazione rotazionale', 'speciali sensori della tensione muscolare', 'organi specializzati per il controllo della gravità e le vibrazioni di bassa frequenza.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (296, 'Nella riproduzione per scissione:', 'I corpo del genitore si divide in 2 o più parti', 'si formano escrescenze su un punto del corpo del genitore', 'si ha perdita di parti seguita da ricostituzione delle parti mancanti', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (297, 'Nella riproduzione per poliembrionia:', 'si ha riproduzione allo stadio larvale', 'l’uovo fecondato si divide nei primi stadi dello sviluppo in più parti', 'I corpo del genitore si divide in 2 o più parti', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (300, 'Nel ciclo di Fasciola epatica quali stadi subiscono l''amplificazione larvale:', 'il miracidio', 'l''adulto nell''intestino dell''ospite', 'le redie e la sporocisti', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (301, 'Quali vantaggi offre la riproduzione asessuale:', 'risparmio energetico e rapida colonizzazione di nuovi ambienti', 'l’omogeneità genetica', 'un’enorme aliquota di variabilità genetica', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (302, 'I gameti sono cellule con un numero di cromosomi pari a:', '4', '2', '', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (303, 'La divisione meiotica consta di:', 'una replicazione del materiale genetico ed una divisione', 'una replicazione del materiale genetico e due successive divisioni', 'due divisioni cellulari in successione', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (304, 'Quale meccanismo cellulare è alla base della riproduzione sessuale:', 'la meiosi', 'la mitosi', 'la scissione', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (305, 'Quale meccanismo cellulare è alla base della riproduzione asessuale:', 'la mitosi', 'la meiosi', 'la gametogenesi', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (306, 'Qual è il prodotto della mitosi:', 'cellule aploidi dette gameti', 'cellule geneticamente identiche ai genitori dette cloni', 'cellule diploidi dette agameti', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (307, 'Negli idrozoi coloniali la gemmazione determina:', 'solo formazione di meduse che si staccheranno dal corpo dell’individuo madre, per condurre vita liberamente natante', 'solo formazione di altri polipi', 'formazione sia di un altro polipo sia di meduse che si staccheranno dal corpo dell’individuo madre, per condurre vita liberamente natante', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (308, 'Le gemmule dei poriferi sono:', 'archeociti rivestiti da spicole e sono prodotte per superare periodi avversi (es. disseccamento).', 'costituite da gruppi di pinacociti', 'gruppi di coanociti che vanno a costituire delle forme di dispersione', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (325, 'Quali meduse possono avere un ciclo biologico abbreviato:', 'specialmente quelle che vivono in zone litorali', 'specialmente quelle di acqua profonda', 'specialmente quelle pelagiche', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (309, 'La gametogenesi è:', 'il processo attraverso il quale per mitosi si moltiplicano i gameti a partire da cellule specializzate (le nadi) a livello di speciali organi detti germinali', 'il processo attraverso il quale per meiosi si formano i gameti a partire da cellule specializzate (germinali) a livello di speciali organi detti nadi', 'il processo attraverso il quale per meiosi si dimezza il numero dei cromosomi nei gameti a partire da cellule specializzate a livello delle nadi', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (310, 'La spermatogenesi ha come prodotto finale:', '4 piccole cellule aploidi, gli spermatozoi', '2 cellule diploidi, gli spermatozoi', '1 grossa cellula aploide, lo spermatozoo', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (311, 'L''ovogenesi ha come prodotto ultimo:', '4 piccole cellule aploidi, gli ootidi', '2 cellule diploidi, gli spermatozoi', '1 grossa cellula aploide, l''uovo', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (312, 'Si definiscono caratterri sessuali primari:', 'tutte le caratteristiche specifiche del sesso che determinano il dimorfismo sessuale', 'testicoli ed ovari', 'Gli apparati sessuali', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (313, 'Cosa sono le spermatofore:', 'sacchetti proteici ricchi di spermatozoi che si aprono nel corpo della femmina.', 'apposite tasche celomatiche in cui risiedono gli spermatozoi prima di essere emessi all''esterno', 'le appendici specializzate di alcuni invertebrati per la deposizione dello sperma nel corpo della femmina', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (314, 'Le uova Telolecitiche presentano:', 'scarse riserve nutritive', 'quantità discreta di tuorlo disposto in posizione eccentrica', 'notevole quantità di riserve; il citoplasma è una piccola ccia galleggiante sul tuorlo', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (315, 'Che tipo di dimorfismo sessuale caratterizza la mantide religiosa:', 'Dimorfismo sessuale primario nel colore.', 'Dimorfismo sessuale secondario nella taglia', 'Dimorfismo sessuale secondario per la presenza di annessi strutturali', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (316, 'La partenogenesi è una particolare strategia riproduttiva in cui:', 'uova fecondate si sviluppano senza l''apporto maschile; anche detta riproduzione monosessuale', 'si sviluppano uova fecondate', 'si sviluppano uova non fecondate; anche detta riproduzione unisessuale', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (317, 'Per partenogenesi arrenotoca:', 'sono prodotte uova che si sviluppano solo in maschi', 'nascono solo femmine', 'nascono sia maschi che femmine', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (318, 'Secondo il modello di partenogensei obbligatoria:', 'occasionalmente alcuni individui sono prodotti per partenogenesi in specie relarmente anfiniche', 'le uova possono svilupparsi sia con che senza fecondazione', 'tutti gli individui di una forma nascono esclusivamente per partenogenesi; tipica delle specie eteroniche', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (319, 'Gli organismi ermafroditi simultanei:', 'presentano una sola nade funzionale che produce solo spermatozoi', 'presentano nadi diverse, funzionanti in tempi diversi (o la stessa nade che si trasforma).', 'presentano entrambe le nadi funzionali nello stesso tempo o un’unica nade indifferenziata, che produce sia spermatozoi, sia uova', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (320, 'Gli organismi ermafroditi sequenziali:', 'presentano nadi diverse, funzionanti in tempi diversi (o la stessa nade che si trasforma).', 'presentano entrambe le nadi funzionali nello stesso tempo o un’unica nade indifferenziata, che produce sia spermatozoi, sia uova.', 'presentano una sola nade funzionale che produce solo uova per partenogenesi', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (321, 'Gli organismi ermafroditi sufficienti:', 'sono incapaci di autofecondazione.', 'sono capaci di autofecondarsi. Essi possono essere obbligati o scegliere facoltativamente l’autofecondazione.', 'sono intersessi in cui sviluppa prima la nade maschile e poi quella femminile', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (322, 'Organismi in cui si sviluppa prima la nade maschile e poi quella femminile sono detti:', 'Ermafroditi sufficienti', 'Ermafroditi simultanei: proteroginici', 'Ermafroditi sequenziali: proterandrici', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (323, 'Il ciclo riproduttivo degli Scifozoi comprende una prima fase larvale:', 'la planula', 'la parenchimula', 'la anfiblastula', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (324, 'Negli Scifozoi la larva (planula) si trasforma in:', 'numerose efire per strobilazione', 'un polipo sessile detto scifistoma.', 'una medusa adulta', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (326, 'Nel ciclo riproduttivo degli Idrozoi:', 'dal nozoide gemmano delle meduse che si riproducono sessualmente', 'gemmano planule che si trasformeranno in polipi', 'gemmano numerose efire per strobilazione', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (327, 'L’idra ha un ciclo riproduttivo:', 'partioclare: la medusa dopo essersi riprodotta, anziché morire, scende sul fondo e si ritrasforma in polipo.', 'ridotto: la fase di medusa è assente', 'completo: la riproduzione asessuale si alterna relaremente a quella asessuale', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (328, 'I molluschi dulciacquicoli presentano:', 'gemmule liberate in autunno come corpi svernanti al termine della stagione d’accrescimento.', 'numerosissime forme larvali', 'larve parassite che si attaccano alle branchie dei pesci.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (329, 'Il nauplio è una forma larvale presente nella maggioranza dei:', 'crostacei', 'molluschi', 'anellidi', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (330, 'Gli organismi che si riproducono solo asessualmente hanno un ciclo biologico:', 'semplice, caratterizzato solo da divisione meiotiche, con scambio genetico.', 'semplice, caratterizzato solo da divisione mitotiche, senza scambio genetico.', 'complesso con alternanza di generazioni', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (331, 'Spongilla lacustris (Poriferi):', 'Si riproduce solo sessualmente durante i mesi estivi.', 'Si riproduce solo asessualmente con formazione di gemmule.', 'Si riproduce sessualmente durante i mesi estivi. Le gemmule sono liberate in autunno come corpi svernanti al termine della stagione d’accrescimento', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (332, 'Quale stadio larvale sguscia dall''uovo di Fasciola epatica:', 'il miracidio', 'la cercaria', 'la metacercaria', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (333, 'In Opistorchis sinensis, a differenza di Fasciola epatica, la cercaria:', 'si attacca alle piante e si trasforma in metacercarie', 'penetra in un pesce in cui s’incista come metacercaria', 'ha una coda bifida (furcocercaria) e penetra attivamente nei tessuti dell’ospite (uomo).', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (334, 'Come avviene l''infestazione di Trichinella spiralis:', 'attraverso insetti ematofagi', 'attraverso ingestione di cibi contaminati dalle uova', 'mediante ingestione di carne in cui sono presenti le larve.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (335, 'La coniugazione è:', 'un fenomeno di sessualità tipico dei Ciliati.', 'una modalità di riproduzione asessuale tipica dei Flagellati.', 'l’alternanza tra riproduzione sessuale ed asessuale tipica dei Foraminiferi.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (336, 'Nel ciclo biologico di Plasmodium sp. la zanzara:', 'È l’ospite definitivo', 'È l’ospite intermedio', 'Non è presente nel ciclo.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (337, 'I Poriferi si riproducono per:', 'Gemmazione e riproduzione sessuale', 'Solo per scissione binaria', 'Per partenogenesi', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (338, 'Lo stadio larvale tipico degli Anellidi è:', 'la palnula', 'la trocofora', 'doliolaria', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (339, 'La muta è:', 'il processo di eliminazione del vecchio esoscheletro (esuvia)', 'il processo di indurimento e scurimento della cuticola neosecreta', 'il processo di accrescimento e cambiamento degli Artropodi', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (340, 'Nella metamorfosi incompleta:', 'la forma adulta è raggiunta progressivamente attraverso una serie di mute.', 'il passaggio dalla larva all’adulto presuppone un sostanziale cambiamento dell’assetto strutturale dell’insetto', 'lo sviluppo è diretto', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (341, 'Gli insetti olometaboli hanno:', 'assenza di metamorfosi', 'una metamorfosi incompleta', 'una metamorfosi completa', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (342, 'Qual''è lo stadio di sviluppo dei bivalvi dulciaquicoli, che è parassita dei pesci?', 'il veliger', 'la trocofora', 'il glochidio', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (343, 'Quali vantaggi offre la riproduzione asessuale?', 'Il risparmio energetico. Non è necessaria la ricerca del partner, né la produzione di gameti.', 'Un’enorme aliquota di variabilità genetica', 'Consente la produzione di organismi di sesso variabile.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (344, 'Nei poriferi le gemme si differenziano a partire:', 'dai pinacociti', ' dagli archeociti', 'dai coanociti', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (348, 'Gli animali con organizzazione triblastica del corpo sono:', 'Esclusivamente acelomati.', 'Esclusivamente acelomati e pseudocelomati', 'Sia Acelomati, pseudocelomati che celomati', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (349, 'Gli Anfibi sono:', 'artropodi prevalentemente terrestri.', 'urocordati, sessili da adulti, solitari o coloniali.', 'vertebrati caratterizzati da una pelle con secrezioni mucose', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (345, 'La corretta sequenza degli eventi nel ciclo biologico di un idrozoo coloniale tipo Obleia è:', 'spermatozoi e uova, larva, medusa, giovane colonia polipoide, colonia adulta', 'meduse, spermatozoi e uova, larva, giovane colonia polipoide, colonia adulta', 'colonia adulta, larva, medusa, giovane colonia', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (346, 'Viene definita aborale:', 'L''estremità opposta all''ano in un animale a simmetria bilaterale', 'L''estremità opposta alla bocca di un animale a simmetria radiale', 'L''estremità di un animale che contiene l''aboro', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (347, 'La mesoglea è:', 'Lo strato più esterno del citoplasma dei sarcodici (amebe)', 'Lo strato intermedio tra l''epiderma e il gastroderma talvolta indicato come mesenchima.', 'Lo scheletro esterno degli Artropodi.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (350, 'La classe Aplacophora (Aplacofori) comprende:', 'artropodi con appendici uniramose ed un paio di zampe per segmento del corpo.', 'molluschi privi di conchiglia, mantello e piede; simili a vermi con capo scarsamente sviluppato.', 'protozoi con ciglia semplici in qualche stadio del ciclo vitale.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (351, 'La cavità del mantello o palliale è:', 'lo spazio tra il manetello e la parete del corpo nei molluschi', 'la cavità del guscio dei protozoi eliozoi definito mantello.', 'la cavità presente nel telencefalo dorsale (pallio), parte del cervello dei vertebrati.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (352, 'Le cercarie sono:', 'larve ciliate liberamente natanti di molti cnidari', 'stadi giovanili dei trematodi digenei.', 'strutture protettive nell''ectoplasma di alcuni ciliati', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (353, 'La notocorda è:', 'una struttura di supporto delle larve di tutti i cordati e molti cordati adulti', 'lo stadio immaturo di un insetto olometabolo.', 'un organo tubulare escretore a forma di corda di molti invertebrati', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (354, 'I parapodi sono:', 'strutture sclerificate per la protezione gli arti degli artropodi', 'estensioni pari laterali su caiscun segmento dei policheti (Anellidi)', 'arti più o meno completi di alcune larve', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (355, 'I pedicelli sono:', 'proiezioni del sistema acquifero di un echinoderma usati principalmente per la locomozione', 'il secondo paio di appendici di artropodi chelicerati', 'piccoli arti delle larve di alcuni crostacei', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (356, 'Le proglottidi sono:', 'primitivi organi osmorelatori od escretori a forma di tubulo', 'i segmenti basali di u''appendice biramosa di un crostaceo', 'Segmenti che costituiscono la strobila di una tenia.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (357, 'I Turbellari sono:', 'una classe di urocordati i cui membri sono planctonici', 'un phylum di animali i cui membri vivono nei sedimenti marini.', 'una classe di platelminti i cui membri sono prevalentemente a vita libera ed acquatici.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (358, 'Con il termine simbiosi intendiamo:', 'il vivere insieme di due specie diverse in una relazione intima', 'la precoce divisione mitotica e citoplasmatica di un embrione', 'la formazione metabolica di calore per contrazione muscolare o metabolisma del grasso bruno', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (359, 'Le spugne fanno circolare acqua attraverso il loro corpo:', 'mai. Non hanno strutture adatte a tal scopo.', 'limitatamente al periodo riproduttivo per veicolare i gameti', 'per assumere cibo e ossigeno e asportare scorie e gameti', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (360, 'Le branchie dermali (o papule) sono:', 'estroflessioni della parete del corpo di una stella di mare funzionali negli scambi gassosi', 'supporti ossei o cartilaginei delle branchie', 'area della superfice del corpo degli anfibi deputate allo scmbio gassoso', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (361, 'L''autotomia è:', 'l''abilità di sintetizzare cibo da composti inorganici', 'l''amputazione volontaria di un appendice.', 'lo stadio della mitosi e meiosi successivo alla metafase', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (388, 'Un taxon è:', 'il metamero finale del corpo degli artropodi.', 'un pigmento respiratorio degli invertebrati.', 'un raggruppamento di organismi distinguibili per caratteri morfologici e genetici.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (390, 'Negli organismi protostomi, il celoma si forma tipicamente per:', 'enterocelia', 'protocelia', 'schizocelia', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (393, 'L''insetto dittero ematofa, vettore del parassita della malaria, appartiene al genere:', 'Aedes', 'Glossina', 'Anopheles', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (362, 'L''ectocotile è:', 'lo strato più esterno di tessuto embrionale.', 'il citoplasma più esterno e viscoso di un protista.', 'un tentacolo modificato dei maschi di alcuni cefalopodi usato nel trasferimento dello sperma', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (363, 'E'' definito stenotermo un organismo:', 'dotato di un elevata tolleranza ecologica nei confronti della temperatura', 'che nel periodo di vita attiva tollera solo un intervallo limitato di temperature ambientali.', 'che non tollera le elevate temperature.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (364, 'Gli ectotermi sono:', 'strutture esterne a forma di aculeo tipiche dello scheletro degli echinodermi', 'enzimi coinvolti nell''ossidazione del grasso bruno', 'animali che perdono rapidamente il calore metabolico', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (365, 'Gli organismi endotermi:', 'conservano il calore metabolico interno e sono in grado di alzare la temperatura corporea molti gradi al di sopra di quella ambientale', 'dissipano il calore metabolico interno per non elevare la temperatura corporea', 'producono calore internamente per elevare la temperatura esterna e migliorare le condizioni vitali', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (366, 'A basse temperature un endotermo deve:', 'evitare di alimentarsi e dissipare calore per non soccombere.', 'trovare cibo a sufficienza per mantenere la produzione di calore elevata e conservare la temperatura del corpo.', 'mantenere la temperatura del copro in accordo a quella ambientale', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (367, 'Cos''è il  "torpore" ?', 'fenomeno di contrazioni isometriche dell’apparato muscolare in condizioni di freddo.', 'una lunga diminuzione stagionale della temperatura.', 'la diminuzione della temperatura corporea per poche ore o giornaliera.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (368, 'Gli scambiatori di calore in controcorrente sono:', 'un sistema per dissipare il calore del corpo', 'un sistema per conservare il calore del corpo', 'un sistema per procurarsi calore dall''ambiente', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (369, 'In un animale il rapporto superficie/volume è:', 'tanto maggiore quanto è più grande di dimensione', 'tanto minore quanto è più piccolo grande di dimensione', 'tanto minore quanto è più grande di dimensione', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (370, 'L''estivazione è:', 'una condizione di dormienza o torpore durante i mesi estivi caldi e secchi.', 'condizione in cui d''estate la temperatura corporea è costante.', 'produzione estiva di forme cistiche di resistenza', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (371, 'La frammentazione:', 'è un tipo di riproduzione sessuale', 'è un tipo di riproduzione asessuale', 'non è una modalità di riproduzione.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (372, 'La gemmazione è:', 'il processo di rigenerazione di parti del corpo (gemme) perse', 'il processo sessuale di formazione di parti del corpo dette gemme.', 'il processo asessuale di formazione di nuovi individui in molti invertebrati.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (373, 'Il miracidio è:', 'il primo stadio larvale di un trematode digeneo che va incontro ad ulteriore sviluppo.', 'lo stadio larvale di molti echinodermi', 'la larva ciliata liberamente natante di molti cnidari', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (374, 'La proterandria è:', 'la condizione in un organismo dioico nel quale le nadi maschili maturano', 'la condizione in un organismo monoico nel quale le nadi maschili maturano prima di quelle femminili', 'lo sviluppo della nade maschile in un organismo', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (375, 'In un organismo proteroginico:', 'c''è assenza anomala delle nadi', 'la nade femminile presenta collegamenti con quella maschile.', 'le nadi femminili maturano prima di quelle maschili.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (376, 'Lo stilo cristallino è:', 'una struttura dell''apparato digerente dei bivalvi che ruota contro lo scudo gastrico e rilascia enzimi digestivi', 'un organo di equilibrio e bilanciamento in molti invertebrati', 'un sottile frammento laminare che forma la copertura di alcuni animali.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (379, 'In quanti tagmi è suddiviso il corpo degli esapodi?', '2', '3', '6', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (380, 'Quante paia di ali hanno la maggioranza degli insetti?', '2', '4', '6', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (381, 'Molti ciliati sono dotati di peculiari organelli estrudibili, in genere ancorati alla superficie cellulare:', 'le spicole', 'le tricocisti', 'i microtrichi', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (385, 'Gli animali "acelomati" comprendono:', ' sia animali diblastici che triblastici.', 'solo animali diblastici.', 'solo animali dblastici', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (377, 'La scissione binaria è:', 'la formazione di escrescenze su un punto del corpo del genitore.', 'divisione del corpo del genitore in più parti.', 'divisione dell’uovo fecondato e nei primi stadi dello sviluppo in più parti.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (378, 'I caratteri sessuali secondari sono:', 'i testicoli e gli ovari', 'caratteri non rilevanti che si sviluppano in alcuni momenti del ciclo vitale', 'caratteristiche specifiche del sesso che determinano il dimorfismo sessuale', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (382, 'In molti animali si sviluppano uova senza essere fecondate (assenza di maschi) che danno origine ad adulti normali. Questa particolare modalità riproduttiva è nota come:', 'gestazione', 'partenogenesi', 'ermafroditismo', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (383, 'I "trilobiti" sono:', 'un subphylum del phylum Arthropoda comprendente animali dal corpo appiattito diviso in lobi.', 'un phylum appartenente alla classe Crustacea, comprendente organismi caratterizzati da 3 paia di antenne lobate.', 'una classe del subphylum Chelicerata, con il primo paio di appendici modificato in lobi carnosi.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (384, 'Gli organismi "protostomi" sono caratterizzati da:', 'segmentazione radiale, formazione dell''ano dal blastoporo e formazione del celoma per enterocelia.', 'segmentazione radiale, bocca che origina dall''ano e celoma che origina per anocelia.', 'segmentazione spirale, bocca che origina dal blastoporo, formazione del celoma per schizocelia', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (386, 'Chi sono gli "Ecdisozoa"?', 'organismi che tipicamente contragno rapporti simbiontici con animali del genere Ecdisa.', 'uno dei due maggiori gruppi di animali protostomi che compiono la muta.', 'quegli organismi caratterizzati da uno stadio larvale tipico: "la larva ecdisia".', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (387, 'Chi sono i "lofotrocozoi"?', 'sono animali che si alimentano di forme larvali tipiche, i "trocozoi", attraverso u''apertura caratteristica,  il "lofo".', 'organismi appartenenti a numerosi phyla caratterizzata da una larva trocofora o dal possesso di un lofoforo utilizzato per l''alimentazione', 'classe di urocordati i cui membri hanno larfe planctoniche e alimentazione per filtrazione.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (389, 'La convergenza evolutiva è:', 'il fenomeno per cui specie diverse che vivono nello stesso tipo di ambiente e sottoposte a stesse pressioni ambientali finiscono per assomigliarsi.', 'l''intersezione di linee filetiche lontane su uno stesso albero evolutivo.', 'il fenomeno per cui due specie convergendo, evolvono assieme diventando interdipendenti.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (391, 'La sementazione spirale è:', 'determinativa (o a mosaico), poichè in fase precoce i balstomeri hanno gia acquisito il loro destino definitivo.', 'determinativa, poiché il numero dei blastomeri è gia stabilito precocemente.', 'indeterminativa, poiché il numero dei blastomeri varia continuamente.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (392, 'La segmentazione radiale è:', 'determinativa (o a mosaico), poiché il numero dei blastomeri è gia stabilito precocemente.', 'indeterminativa (o relativa), poiché in fase precoce i blastomeri non hanno ancora acquisito il loro destino definitivo.', 'indeterminativa, poiche quando si esplica, il numero dei blastomeri non è determinato.', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (394, 'Quali delle seguenti affermazioni relative alla balena è ESATTA?:', 'Il più grande teleosteo del mondo', 'Allatta la prole', 'E'' un pesce a sangue caldo', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (395, 'Il nome scientifico dell''uomo è "Homo sapiens". La parola sapiens indica:', 'la specie', 'il genere', 'l''ordine', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (466, 'Quali delle seguenti strutture svolno un ruolo difensivo nei protozoi ciliati?', 'pellicola', 'tricocisti', 'meristemi', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (467, 'Quale delle seguenti classi di Platelminti non annovera specie parassite?', 'Monogenea', 'Trematoda', 'Turbellaria', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (468, 'Nel sistema a digametia maschile il maschio?', 'è portatore di 2 cromosomi sessuali diversi', 'è caratterizzato da un numero doppi di cromosomi sessuali', 'produce un numero doppio di gameti', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (396, 'Quale di queste caratteristiche è più adatta per accorpare un gruppo di animale alla medesima specie:', 'lo stesso numero cromosomico', 'caratteristiche mofologiche simili', 'capacità di incrociasi tra loro e riprodursi', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (397, 'L''uomo è:', 'un emicefalocordato', 'un urocordato', 'un cefalocordato', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (398, 'Due organismi apparten a specie differenti se:', 'hanno diverso tasso di mutazione a causa del diverso ambiente', 'non possono accoppiarsi tra loro producendo prole fertile', 'hanno geni in comune dello stesso pool genico', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (399, 'Un parassita come la tenia è un esempio di organismo ermafrodita perché:', 'si sviluppa con metamorfosi', 'si riproduce asessualmente', 'ha organi riproduttori maschili e femminili', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (400, 'Quali dei seguenti organismi è sprovvisto di vertebre:', 'Arasta', 'Vipera', 'Squalo', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (401, 'La sanguisuga è un:', 'mollusco', 'verme', 'pesce tropicale', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (402, 'Quali animali mantenno costante la loro temepratura interna?', 'pesci e mammiferi', 'rettili e pesci', 'uccelli e mammiferi', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (403, 'Se i quattro stadi che caratterizzano lo sviluppo di un insetto a metamorfosi completa fossero messi in ordine temporale, quale sarebbe la sequenza corretta', 'uovo/larva/crisalide/adulto', 'larva/crisalide/adulto/uovo', 'uovo/crisalide/larva adulto', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (404, 'Quale delle seguenti affermazioni è corretta?', 'la classe è un gruppo di phyla', 'il genere è un gruppo di specie correlate', 'il genere è la principale suddivisione di una specie', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (405, 'Quali dei seguenti animali non è un rettile', 'Coccodrillo', 'Tartaruga', 'Salamandra', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (406, 'Qual è la sequenza esatta delle caterie tassonomiche (dalla più ampia alla più ristretta)?', 'ordine-famiglia-genere-specie', 'genere-specie-ordine-famiglia', 'specie-famiglia-ordine-genere', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (407, 'Quali dei seguenti animali è un vertebrato?', 'Calamaro', 'Trota', 'Arasta', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (408, 'I Procarioti sono:', 'organismi primitivi capaci di metabolismo ma non di riproduzione', 'organismi unicellulari protozoi e protofiti', 'organismi privi di strutture definite membrane interne', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (409, 'La definizione biologica di specie si fonda:', 'sull''isolamento riproduttivo di due gruppi di organismi', 'sulle differenze genotipiche tra due organismi', 'sulle differenze anatomiche e di sviluppo tra due gruppi di organismi', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (410, 'Qual’è la principale differenza tra una cellula eucariota e procariota?', 'tutte le cellule eucariote sono multicellulari, tutte quelle procariote sono unicellulari', 'quelle eucariote hanno membrane intorno al nucleo, le procariote no.', 'i batteri sono cellule eucariote, tutti gli altri organismi viventi sono eucarioti con nucleo eccetto i procarioti', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (411, '	Le api attuano una danza molto complessa per:', 'attrarre  e conquistare partners sessuali', '	comunicare agli altri membri del gruppo dove trovare cibo', 'spaventare i predatori', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (412, '	Che cos’è la neotenia?', 'Un termine usato per indicare che le larve sono più piccole degli adulti', 'Uno stadio della metamorfosi degli anfibi', 'Quando un animale adulto ritiene delle caratteristiche larvali', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (413, '	Il ginandromorfismo è:', 'Condizione per cui un individuo è costituito da parti con caratteri maschili e altre con caratteri femminili.', 'Uno stadio della metamorfosi degli insetti olometaboli', 'Un termine usato per indicare che le larve sono più piccole degli adulti.', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (414, 'Quale phylum è caratterizzato da coanociti che creano una corrente per internalizzare particelle alimentari?', 'Porifera', 'Cnidaria', 'Platyhelmintes', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (415, '	In quale organismo sotto elencato puoi trovare tipicamente delle setole?', 'lombrico', 'stella marina', 'verme piatto', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (416, '	Quale dei seguenti phyla di "vermi" possiede una cuticola esterna dura che subisce ecdisi?', 'Anellida', 'Nematoda', '	Platyhelminthes', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (417, '	Quale delle seguenti classi di organismi ha membri che contragno rapporti mutualistici con zooxantelle (alghe simbionti)?', 'Cl. Aves', 'Cl. Hydrozoa', 'Cl. Anthozoa', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (418, '	Quale dei seguenti phlya è il più grande conosciuto al mondo (in termini di numero di specie)?', 'Arthropoda', 'Chordata', 'Cnidaria', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (419, '	Se rimuoviamo delle cellule da un embrione precoce di organismi "deuteostomi" le rimanenti cellule:', 'non sono in grado di proseguire nello sviluppo', 'sono comunque in grado di sviluppare un embrione completo', 'raddoppiano tipicamente il loro numero', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (420, 'Per gli animali uno dei vantaggi di possedere un vero "celoma" è:', 'gli organi sono sempre umidi perché il celoma è pieno di liquido.', 'il vero celoma è tappezzato da mesoderma che non lascia fuoriuscire liquidi corporei.', 'l’animale può muovere i muscoli esterni al corpo senza interferire con la digestione.', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (421, '	Il nome scientifico del leone è Panthera leo. Quale parte del nome indica la famiglia?', 'Panthera', 'leo', 'Nessuna delle due', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (422, '	Alcuni organismi hanno una cavità del corpo con mesoderma che riveste la parete del corpo ma che non riveste gli organi interni. Questi organismi venno definiti:', 'Pseudocelomati', 'Acelomati', 'Eucelomati', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (423, '	Sinle cellule eucariote come Euglena sp. Ed Amoeba sp. A quale taxon appartenno:', 'Archaebacteria', 'Protista', 'Eubacteria', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (424, 'Le nematocisti sono strutture deputate a quale funzione?', 'riproduzione', 'percezione sensoriale', 'difesa/offesa', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (425, 'Gli organismi ermafroditi insufficienti, prevenno l''autofecondazione essendo:', 'proterandrici o proteroginici', 'monoici', 'ginandromorfi', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (426, 'L''efira è la forma larvale di quale di questi organismi?', 'Obelia', 'Aurelia', 'Hydra', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (427, 'Cnidari polimorfici appartenno alla classe:', 'Actinozoa', 'Scyphozoa', 'Hydrozoa', 'c');
