INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (532, 'I tre foglietti embrionali (ecto, endo, mesoderma) si ritrovano in:', 'Tutti i cordati, eccetto i protocordati', 'sia nei cordati che negli invertebrati', 'solo negli invertebrati', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (533, 'La simmetria bilaterale è tipica di:', 'soloo Cordati', 'Solo vertebrati', 'anellidi, artropodi e vertebrati', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (534, 'In quali dei seguenti organismi la corda non persiste nell''animale adulto?', 'Tunicati', 'Anfiosso', 'Ciclostomi', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (535, 'Cosa condividono Vertebrati e Tunicati?', 'la formazione di strutture per metamorfosi', 'una notocorda e un tubo nervoso dorsale cavo', 'un forte grado di cefalizzazione', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (536, 'Quali delle seguenti caratteristiche è probabilmente stata responsabile della grande diversificazione degli insetti in ambiente terrestre?', 'segmentazione', 'antenne', 'esoscheletro', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (537, 'Quali delle seguenti combinazioni e/o descrizioni è sbagliata?', 'Calcisponge, cavità gastrovascolare, celoma', 'Echinodermi, larva a simmetria bilaterale, celomati', 'Nematodi, vermi cilindrici, pseudocelomati', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (538, 'Quali delle seguenti combinazioni e/o descrizioni è sbagliata?', 'Protozoi, unicellulari, a volte coloniali', 'Poriferi, celomati, segmentati', 'Platelminti, vermi paitti, acelomati', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (539, 'La capacità di un organismo di imitarne un altro, allo scopo di trarne vantaggiom si chiama:', 'analogia', 'adattamento', 'mimetismo', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (540, 'Quale delle seguenti affermazioni è vera riguardo la cavità del mantello di un bivalve?', ' E ''la zona dove l''acqua circola durante lo scambio gassoso e l''escrezione', ' E ''la zona dove l''acqua circola durante lo scambio gassoso e l''escrezione', 'È lo spazio tra il pericardio e il cuore', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (541, 'Quale delle seguenti affermazioni è vera riguardo la massa viscerale di un mollusco?', 'contiene le branchie', 'contiene nadi e le ghiandole digestive', 'secerne la conchiglia', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (542, 'I membri di quale delle seguenti classi di molluschi sono caratterizzati da un guscio composto da 8 piastre?', 'Aplacophora', 'Polyplacophora', 'Cefalopodi', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (543, 'L''acqua esce dalla cavità del mantello di un bivalve attraverso:', 'la camera soprabranchiale', 'la cavità pericardica', 'il sifone esalante', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (544, 'La superficie dorsale di una rana è:', 'la sua estremità posteriore', 'la schiena', 'la pancia', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (545, 'Gli animali triblastici i cui tessuti derivanti dal mesoderma formano una massa relativamente solida di cellule tra ectoderma ed endoderma sono detti:La(e) principale(i) struttura(e) propulsiva(e) del sistema circolatorio dei molluschi è(sono):', 'acelomati', 'pseudocelomati', 'celomati', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (546, 'Un piano trasversale divide un animale bilaterale i', 'immagini speculari, destra e sinistra.', '	in metà identiche', 'in metà anteriore e posteriore', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (547, 'La riproduzione nei protozoi comporta:', 'Solo modalità di scissione binaria', 'riproduzione solo sessuata', 'c. sia riproduzione asessuale che sessuale', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (548, 'La regione specializzata in protozoi che è analoga a una bocca è:', 'il vacuolo alimentare', 'il citostoma', 'il citopigio', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (549, 'La malattia del sonno nell''uomo sonno è causata da:', 'un tripanosoma', 'un ameba', 'un euglenide', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (550, 'I ciliati si riproducono sessualmente per:', 'scissione binaria', 'coniugazione', 'gemmazione', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (551, 'I primi animali originarono da cellule multinucleate che successivamente formarono membrane plasmatiche all''interno della cellula per produrre un piccolo organismo multicellulare. Questa idea è nota come ipotesi:', 'coloniale', 'plasmodiale', 'precambriana', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (552, 'Le seguenti sono tutte caratteristiche dei membri del phylum Porifera tranne uno. Selezionare l''eccezione', 'asimmetrici o a simmetria radiale', 'organizzazione a due strati', 'hanno tre tipi di cellule', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (553, 'Le spugne più complesse hanno l''organizzazione:', 'leuco', 'syco', 'myco', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (554, 'Quale delle seguenti strutture non fa parte del sistema protonefridiale in un turbellare?', 'le spugne cornee', 'le spugne calcaree', 'le spugne vitree', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (555, 'La riproduzione asessuale nei protozoi comprende le seguenti modalità ad eccezione di:', 'coniugazione', 'schizonia', 'gemmazione', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (556, 'Il genere Vorticella comprende specie di protozoi:', 'vagili', 'che si attaccano al substrato mediante un peduncolo contrattile', 'parassiti dell''uomo e di altri vertebrati', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (557, 'La larva, detta idatide, capace di riproduzione asessuale per gemmazione interna è presente:', 'nel botriocefalo', 'nella tenia', 'nell''echinococco', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (558, 'La criptobiosi dei nematodi è un fenomeno per cui:', 'si possono alimentare mentre scavano', 'si disidratano e sono capaci di resistere a condizioni avverse sospendendo tutte le attività vitali', 'entrano in simbiosi con altri organismi', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (559, 'I nematodi sono organismi:', 'la cui forma del corpo può essere molto variabile tra le specie', 'ermafroditi, con rare eccezioni di specie dioiche', 'dioici, con rare eccezioni di specie ermafrodite', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (600, 'Gli opistobranchi sono:', 'gasteropodi marini che presentano molto sspesso la riduzione della conchiglia', 'cefalopodi con conchiglia ridotta', 'lamellibranchi con conchiglia a 2 valve simmetriche', 'a');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (601, 'I nudibranchi:', 'hanno 2 paia di branchie nella cavità del mantello', 'presentano branchie interne in numero variabile non rivestite da epitelio', 'respirano attraverso i cerati', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (602, 'Nel ciclo dei molluschi dulciacquicoli (Unio spp., Anodonta spp.):', 'si verifica il fenomeno dell''amplificazione larvale', 'c''è una metagenesi', 'si sviluppa una larva detta glochidio, parassita delle branchie dei pesci', 'c');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (607, 'Negli echinodermi la simmetria raggiata è:', 'primaria', 'secondaria', 'terziaria', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (603, 'I ragni presentano 6 paia di appendici articolate che nell''ordine antero-posteriore sono:', 'pedipalpi, cheliceri e 4 paia di zampe', 'cheliceri, pedipalpi e 4 paia di zampe', 'cheliceri, 4 paia di zampe e pedipalpi', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (604, 'Quale delle seguenti descrizioni corrisponde agli ortotteri:', 'ali di uguale lunghezza, membranose e provviste di robuste nervature; addome molto allungato e tubolare', 'primo paio di zampe simili al secondo, zampe posteriori particolarmente sviluppate e adattate al salto', 'capo molto mobile e collegato al torace mediante un collo sottile, torace con mesotorace più sviluppato', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (605, 'Il sistema nervoso degli insetti è costituito da:', 'un encefalo da cui si dipartono i nervi che vanno in tutto il corpo', 'un encefalo, cui segue una catena gangliare ventrale', 'un encefalo da cui partono 2 rami nervosi posteriori, uno dorsale e uno ventrale', 'b');
INSERT INTO domande (id, domanda, a, b, c, giusta) VALUES (606, 'Negli insetti ametaboli:', 'il neonato è diverso dall''adulto e la forma adulta è raggiunta progressivamnete attraverso una serie di mute', 'alla schiusa il neonato è molto simile all''adulto', 'dopo una serie di mute larvali passano attraverso uno stadio intermedio (pupa), in cui avviene la metamorfosi', 'b');
